warp:
shader_body
{
    // sample previous frame
    ret.xyz = texture( sampler_main, fract(uv) ).xyz;

      // take the difference between the crisp and blurred images,
      // then add it back into the image.  Creates spots and stripes over time.
      vec3 b = mix(((texture(sampler_blur2, uv).xyz * scale2) + bias2),((texture(sampler_blur1, uv).xyz * scale1) + bias1),uv_orig.x);//((texture(sampler_blur2, uv).xyz * scale2) + bias2);
      ret.xyz += (ret.xyz - b)*0.3 - 0.04;
      ret.xyz *= 0.95;

      // add noise:
      vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
      ret.xyz += 0.04;//(texture(sampler_noise_lq, dither_uv).xyz-0.5)*0.3;

      // desaturate over time, to keep the globs white
      ret.xyz = mix(ret.xyz, lum(ret.xyz), 0.2);
}

comp:
shader_body
{

vec2 uv1 = uv-.5;

vec3 ret1 = texture(sampler_main, uv).xyz +texture(sampler_main, 1-uv).xyz*0;


vec2 hor = vec2 (texsize.z,0);
vec2 ver = vec2 (0,texsize.w);
float dx = texture(sampler_main, uv-hor).xyz - texture(sampler_main, uv+hor).xyz;
float dy = texture(sampler_main, uv-ver).xyz - texture(sampler_main, uv+ver).xyz;
vec2 dz = vec2 (dx,dy);

uv1 = .3*cos(uv1*2+1.7) -2*dz;;
vec3 dots = clamp(.03/length(uv1), 0.0, 1.0);


ret1 = (-ret1/4) + 6*dots*(-0.08+ret1);

ret =ret1;
ret /= vec3(1.3,2.1,0.6);
}

