warp:
shader_body
{


float error     = 0.3;        // maximum random pixel offset
float diffusion = 0.004;      // dither strength
float fadeout   = -0.001;    // may be minor 1/256 due to the error diffusion dither;

vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw*1.5 + rand_frame.xy;
float magic = lum(texture(sampler_main, mix(uv_orig,uv, 4)).xyz) - lum(((texture(sampler_blur1, mix(uv_orig,uv,-8)).xyz * scale1) + bias1));
uv = mix(uv_orig,uv, magic*8 ) + (texture(sampler_noise_lq, dither_uv).xyz-0.5)*texsize.zw*error;
ret = texture(sampler_main, uv).xyz+(texture(sampler_noise_lq, dither_uv).xyz-0.5)*diffusion + fadeout;

}

comp:
shader_body
{

vec2 uv1 = uv-.5;

vec3 ret1 = texture(sampler_main, uv).xyz +texture(sampler_main, 1-uv).xyz*0;


vec2 hor = vec2 (texsize.z,0);
vec2 ver = vec2 (0,texsize.w);
float dx = texture(sampler_main, uv-hor).xyz - texture(sampler_main, uv+hor).xyz;
float dy = texture(sampler_main, uv-ver).xyz - texture(sampler_main, uv+ver).xyz;
vec2 dz = vec2 (dx,dy);

uv1 = .3*cos(uv1*2+1.7) -2*dz;;
vec3 dots = clamp(.03/length(uv1), 0.0, 1.0);


ret1 = (-ret1/4) + 6*dots*(-0.08+ret1);

ret = ret1;
ret /= vec3(0.6,1.4,1.0);
ret /= vec3(0.9,2.0,-1.3);
}

warp_hlsl:
shader_body
{


float error     = 0.3;        // maximum random pixel offset
float diffusion = 0.004;      // dither strength
float fadeout   = -0.001;    // may be minor 1/256 due to the error diffusion dither;

float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw*1.5 + rand_frame.xy;
float magic = lum(GetPixel(lerp(uv_orig,uv, 4))) - lum(GetBlur1(lerp(uv_orig,uv,-8)));
uv = lerp(uv_orig,uv, magic*8 ) + (tex2D(sampler_noise_lq, dither_uv)-0.5)*texsize.zw*error;
ret = GetPixel(uv)+(tex2D(sampler_noise_lq, dither_uv)-0.5)*diffusion + fadeout;

}

