comp:
vec2 rs, rs0, uv2, uv3,uv4,dz;
vec3 noise, ret1, neu;
vec2 hor = vec2 (texsize.z,0);
vec2 ver = vec2 (0, texsize.w);

shader_body {
vec2 uv1 = (uv-.5 )*aspect.xy;

float z = .1/uv1.y/uv1.x;

rs0.x = uv1.x*z;
rs0.y = z;
rs.x = rs0.x + time/2;
rs.y = rs0.y + time/2;

noise = lum(texture(sampler_noise_hq,rs/2).xyz);
noise = noise-.55;

vec3 mask = clamp(24*(uv1.y), 0.0, 1.0) ;



float dist = 1;
float inten = 1;
ret1 = 0;
int anz = 4;
int n = 1;
dz = 0;
while (n <= anz) {
    dist = 1-fract(1.0/anz*n+time/18); //evtl sqrt !;

    inten =  sqrt(dist)*(1-dist)*4;
    uv = uv1*aspect.yx;
    uv3 = uv*dist +.5 + vec2(0.02*((n%2)-.5),.05);

    dz.x += inten*(2*lum(texture(sampler_main, uv3+hor).xyz)- 2*lum(texture(sampler_main, uv3-hor).xyz));
    dz.y += inten*(2*lum(texture(sampler_main, uv3+ver).xyz)- 2*lum(texture(sampler_main, uv3-ver).xyz));
    neu = texture(sampler_main, uv3).xyz;
    ret1 = max(ret1,neu*inten);
n++;
}

dz = (dz*(.5+rand_preset.z));
vec2 pos = 2*(rand_preset.xy-.5);
uv1 = 4*uv1;
vec2 uv_rr = sin(uv1 + dz + pos);
vec2 uv_gg = sin(uv1 + dz*1.4 + pos);
vec2 uv_bb = sin(uv1 + dz*1.8 +pos);
vec3 size = vec3 (.01,.01,.01) * (1+rand_preset.xyz/2)*(.5+rand_preset.y);
vec3 mus = vec3(1/length(uv_rr),1/length(uv_gg),1/length(uv_bb))*size;

ret = mus*((rand_preset.x-.5)*4*ret1+8*(1+rand_preset))
-  ret1.r *.5 + (ret1.g + ret1.b)/3 ;
ret-=slow_roam_sin.wzy*roam_cos.zxy*.4;
ret *= 1+ret;
//ret = texture(sampler_main, uv_orig+.5).xyz;
ret /= vec3(-0.4,1.5,1.8);
}

