ret = mix (
((texture(sampler_blur1, uvm).xyz * scale1) + bias1).x + ((texture(sampler_blur3, uvm).xyz * scale3) + bias3).x,
ret,
pow(ret,.5));

ret *= .9;

//  ret = texture(sampler_main, uv).x - ((texture(sampler_blur1, uv).xyz * scale1) + bias1).x;
