q1 = pvol;

//Zentrum
q10 = 0.5; 
q11 = 0.5;
q32 = aspecty;

q3 = gmegabuf(50000)+1; 
q4 = gmegabuf(50005)+1; 

//planet pos
t1 = time/6 ;
q15 = 0.5-0.35*sin(t1);
q16 = 0.5- (0.05+0.24*cos(t1) * (0.7+0.3*sin(t1/7))); 


monitor = vol;

warp:
const vec2 centre = vec2 (q10-0.5,-(q11-0.5))/aspect.xy+0.5;

shader_body {
ret = mix(texture(sampler_main, uv).xyz,((texture(sampler_blur1, uv).xyz * scale1) + bias1),0.2)*0.75;
vec2 uv2 = uv-centre+0.5;
       uv2 = vec2 (1-uv2.x,uv2.y)-0.5;
uv2 = (uv2)*0.8 + centre;
ret = max (ret, mix(texture(sampler_main, uv2).xyz,((texture(sampler_blur1, uv2).xyz * scale1) + bias1),0.4)*0.55);

ret = ret* (1.1-pow(rad,2)*1) - 0.002;
}

comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz;
    ret += ((texture(sampler_blur1, uv).xyz * scale1) + bias1);
    ret *= 2.90; //gamma
}

warp_hlsl:
static const float2 centre = float2 (q10-0.5,-(q11-0.5))/aspect.xy+0.5;

shader_body {
ret = lerp(GetPixel(uv),GetBlur1(uv),0.2)*0.75;
float2 uv2 = uv-centre+0.5;
       uv2 = float2 (1-uv2.x,uv2.y)-0.5;
uv2 = (uv2)*0.8 + centre;
ret = max (ret, lerp(GetPixel(uv2),GetBlur1(uv2),0.4)*0.55);

ret = ret* (1.1-pow(rad,2)*1) - 0.002;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret += GetBlur1(uv);
    ret *= 2.90; //gamma
}
