#define sat saturate
#define snl sampler_noise_lq
#define snh sampler_noise_hq
uniform sampler2D sampler_heart;
uniform sampler2D sampler_pw_noise_lq;
vec3 neon, neons, col;
vec2 water, uv0, uv1, uv2, uv5, dz, tuv0, tuv, tshift;
float near, dist, noise, z, k1, ftu, nshape, nmask, iswater;
const int anz = 0, tubes = 12;
float trad = 0.01, tlen = 2;; 

float tria (float x) {return abs(2*fract(x/2)-1);}

shader_body
{
uv = (uv-0.5)*q16+0.5;
uv += vec2(q17,q18);
uv0 = uv;
uv1 = (uv-0.5)*aspect.xy ;
z = (0.1 + 0.3*slow_roam_sin)/(uv1.y);
uv2 = vec2 (z*(uv1.x),z+q19);
z  *= 1+0.1*q11*sin(uv2.y+q10*M_PI);
uv2 = vec2 (z*(uv1.x),z+q19);

water = texture (snh, uv2).xyz + texture (snh,uv2*2+vec2(0,q19/2)).xyz/2-0.7;
iswater = (z>0)* sat((5-15*abs(uv1.x)*z) /z);
uv += water * 0.04 *iswater;

//NEON
uv1 = (uv-0.5)*aspect.xy ; 
tuv0 = vec2 (.5/length (uv1), atan(uv1.x,-uv1.y)/M_PI) ;
dist = tuv0.x;
near = sat (2-dist/4);
tuv = tuv0 + vec2(q19,0);

ftu = fract (tuv.y*tubes/2)-0.5; 
nshape = clamp(1-1/trad*pow (ftu,2), 0.0, 1.0);
tuv.x -= nshape*2.5*sqrt(trad)/tubes*tuv0.x;
k1 = abs(sin(tuv.x/tlen*2*M_PI));
nshape *= sat(k1*2);
uv5  = tuv*vec2(0.25/tlen,tubes/16.0) + 0*vec2(q29,q30)/8; 
col = (texture(sampler_pw_noise_lq,uv5/32).xyz+0.2);

neon = pow(nshape,0.41)*sat(8*k1)*col;
neons= sat(k1)* clamp(exp(-4*abs(ftu)), 0.0, 1.0) * col ;

nmask = fract((uv5.x*8.0*q20-q21)) < q22;
nmask *=  sat(tria((uv5.y*16.0+1)/q25+q27*2)*q26 -q26+1) ; 
nmask += 0.1;

noise =  texture(snl,tuv*2).xyz- texture(snl,tuv*2+2*texsize.zw).xyz*0.8;
noise += texture(sampler_heart,tuv*vec2(0.5,2)).g/-6 ;
noise *= (nshape==0) * near;


ret = texture(sampler_main, uv).xyz * (uv.x<1) * (uv.x>0)* (uv.y<1) * (uv.y>0);
ret += near/2*(nshape>0)*((texture(sampler_blur1, uv -ftu*(nshape>0)).xyz * scale1) + bias1);
ret += (neon + 6*neons*noise.x)* near*nmask;

//ret += texture(sampler_main,  fract((ftu*!(nshape==0)+tuv0-vec2 (-16,2))*0.25*vec2(0.1,1))).xyz/2*mix(noise.x,1,nshape) ;

//Hintergrundbel.
uv2 = fract ((uv1)*0.1*rad+0.5 +ftu * !(nshape==0));
ret += mix (texture(sampler_main, uv2).xyz, texture(sampler_main, uv2).xyz, nshape>0)*(noise*mid_att +nshape * near/2);


vec3 shine = ret;
float radi=1; float anz = 20; 
float scale=1.5/anz;
for (int n=1; n <= anz; n++)
{ 
  shine += scale*((texture(sampler_blur1, (uv0-0.5)*radi+0.5).xyz * scale1) + bias1) ;
  radi -= .9/anz ;
}


ret = shine * (1-sat(5*iswater)*0.2);

//ret = ret*0.2+nmask/2;

}

