warp:
shader_body
{


float error     = 0.45;        // maximum random pixel offset
float diffusion = 0.0043;      // dither strength
float fadeout   = -0.0015;    // may be minor 1/256 due to the error diffusion dither;

vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw*1.5 + rand_frame.xy;
float magic = lum(texture(sampler_main, mix(uv_orig,uv, 8)).xyz) - lum(((texture(sampler_blur1, mix(uv_orig,uv,-8)).xyz * scale1) + bias1));
uv = mix(uv_orig,uv, magic*0.75 ) + (texture(sampler_noise_lq, dither_uv).xyz-0.5)*texsize.zw*error;
ret = texture(sampler_main, uv).xyz+(texture(sampler_noise_lq, dither_uv).xyz-0.47)*diffusion + fadeout;

}

comp:
shader_body
{

vec2 uvm = uv;
ret = texture(sampler_main, uvm).x,

ret = mix (
((texture(sampler_blur1, uvm).xyz * scale1) + bias1).x + ((texture(sampler_blur3, uvm).xyz * scale3) + bias3).x,
ret,
pow(ret,.3));

ret *= 1.7;

vec2 uv3 = (uv-.5)*(1-((texture(sampler_blur1, uvm).xyz * scale1) + bias1).zzz)+.5;

ret += clamp(pow(texture(sampler_main, uv3).zzz,.5)*(1-((texture(sampler_blur1, uvm).xyz * scale1) + bias1).x -ret.x), 0.0, 1.0),
ret += clamp(pow(((texture(sampler_blur1, uv3).xyz * scale1) + bias1).zzz,0.43)*(1-((texture(sampler_blur1, uvm).xyz * scale1) + bias1).x -ret.x), 0.0, 1.0),

ret = pow(ret,vec3(1.3,1,0.9))*1.35 -.25;
ret -= texture(sampler_main, uvm).yyy;
ret *= ret;

ret-= .07;
vec4 noise9 = texture(sampler_noisevol_hq, 
                      ((uv3.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(0.6,0.7,0.2)*0.05 + 
                      time*vec3(2.2,0.9,1.6)*q30 
                     ).xyz;
if(ret.x> q26*q3         && ret.x<= q23*q1        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q1         && ret.y<= q22*q2        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q2         && ret.z<= q21*q3        )ret.y -= (noise9).z*.5; 
ret -= 0.21;
ret /= vec3(1.4,0.8,1.4);
}

warp_hlsl:
shader_body
{


float error     = 0.45;        // maximum random pixel offset
float diffusion = 0.0043;      // dither strength
float fadeout   = -0.0015;    // may be minor 1/256 due to the error diffusion dither;

float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw*1.5 + rand_frame.xy;
float magic = lum(GetPixel(lerp(uv_orig,uv, 8))) - lum(GetBlur1(lerp(uv_orig,uv,-8)));
uv = lerp(uv_orig,uv, magic*0.75 ) + (tex2D(sampler_noise_lq, dither_uv)-0.5)*texsize.zw*error;
ret = GetPixel(uv)+(tex2D(sampler_noise_lq, dither_uv)-0.47)*diffusion + fadeout;

}

comp_hlsl:
shader_body
{

float2 uvm = uv;
ret = tex2D(sampler_main, uvm).x,

ret = lerp (
GetBlur1(uvm).x + GetBlur3(uvm).x,
ret,
pow(ret,.3));

ret *= 1.7;

float2 uv3 = (uv-.5)*(1-GetBlur1(uvm).zzz)+.5;

ret += saturate(pow(tex2D(sampler_main, uv3).zzz,.5)*(1-GetBlur1(uvm).x -ret.x)),
ret += saturate(pow(GetBlur1(uv3).zzz,0.43)*(1-GetBlur1(uvm).x -ret.x)),

ret = pow(ret,float3(1.3,1,0.9))*1.35 -.25;
ret -= tex2D(sampler_main, uvm).yyy;
ret *= ret;

ret-= .07;
float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv3.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(0.6,0.7,0.2)*0.05 + 
                      time*float3(2.2,0.9,1.6)*q30 
                     );
if(ret.x> q26*q3         && ret.x<= q23*q1        )ret.z -= (noise9).x*.5; 
