warp:
// Shader by altunenes https://www.shadertoy.com/view/DtXyWM 
// Ported to MilkDrop by MilkDrop2077

// Constants
#define P 3.14159265359

// Rodrigues' rotation formula
vec3 e(vec3 p, vec3 ax, float ro) {
    return mix(dot(p,ax)*ax, p, cos(ro)) + sin(ro)*cross(ax,p);
}

// 3D Voronoi
vec3 v3d(vec3 x) {
    vec3 p = floor(x);
    vec3 f = fract(x);
    vec2 res = vec2(2.0, 2.0);

    for(int k=-1; k<=1; k++) {
        for(int j=-1; j<=1; j++) {
            for(int i=-1; i<=1; i++) {
                vec3 r = vec3(i,j,k) - f;
                float d = dot(r,r);
                if(d < res.x) {
                    res.y = res.x;
                    res.x = d;
                }
                else if(d < res.y) {
                    res.y = d;
                }
            }
        }
    }
    return vec3(sqrt(res.x), sqrt(res.y), 0.0);
}

shader_body {
    // Coordinate setup
    float aspect = aspect.x/aspect.y;
    vec2 p = (uv.xy-0.5)*vec2(aspect,1.0);
    float t = time*0.6;
    float s = sin(t);
    float r = 5.0 + s*0.01;
    float S = 15.0;
    float a = 0.0;
    vec2 n = vec2(0,0);

    // Animation loop
    for(float j=1.0; j<15.0; j++) {
        p = e(vec3(p,0), vec3(0,0,0.96), r).xy;
        n = e(vec3(n,0), vec3(0,0,1), r).xy;
        vec2 q = p*S + t*3.5 + s + j + n;
        a += dot(cos(q+q19)/S, vec2(0.31,0.31));
        n -= sin(q);
        S *= 1.2;
    }

    // Color calculation
    float f = 0.17*(a + 0.2 + a + a);
    uv = uv.xy;
    vec3 v = v3d(vec3(t, uv.x, uv.y));
    float w = cos(5.0*t + e(vec3(uv,0), vec3(0,0,1), t*0.1).y*uv.x*P);
    vec3 c = 1.3 + 0.5*sin(2.0*t + vec3(0,2.09,4.19) + 2.0*w);

    // Final output
    ret = vec4(pow(v.r*0.1, f*30.0)*1.5*(c + vec3(0.5,0.1,0.8)*0.1), 1.0);
}

