warp:
shader_body
{
    // sample previous frame
    ret =texture( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret *= 0.94; //or try: ret -= 0.004;
}

comp:
shader_body
{
    ret =texture(sampler_main, uv).xyz;
    ret =ret*1.5 - 0.1;

    vec4 g;
    vec3 d = vec3(texsize.zw * 4, 0);
    g.x = lum(((texture(sampler_blur1, uv + d.xz).xyz * scale1) + bias1));
    g.y = lum(((texture(sampler_blur1, uv - d.xz).xyz * scale1) + bias1));
    g.z = lum(((texture(sampler_blur1, uv + d.zy).xyz * scale1) + bias1));
    g.w = lum(((texture(sampler_blur1, uv - d.zy).xyz * scale1) + bias1));

    ret *= 0.7;
    ret.x += clamp(g.x-g.y, 0.0, 1.0)*5;
    ret.z += clamp(g.y-g.x, 0.0, 1.0)*5;
    //ret.xy += (g.xz-g.yw)*3;

ret.x = ((texture(sampler_blur1, uv).xyz * scale1) + bias1).x*2;
ret.z = ((texture(sampler_blur2, uv).xyz * scale2) + bias2).z*3;

}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret *= 0.94; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret = ret*1.5 - 0.1;

    float4 g;
    float3 d = float3(texsize.zw * 4, 0);
    g.x = lum(GetBlur1(uv + d.xz));
    g.y = lum(GetBlur1(uv - d.xz));
    g.z = lum(GetBlur1(uv + d.zy));
    g.w = lum(GetBlur1(uv - d.zy));

    ret *= 0.7;
    ret.x += saturate(g.x-g.y)*5;
    ret.z += saturate(g.y-g.x)*5;
    //ret.xy += (g.xz-g.yw)*3;

ret.x = GetBlur1(uv).x*2;
ret.z = GetBlur2(uv).z*3;

}
