frame_eqs_eel:
dec_med = pow (0.69, 30/fps);
dec_slow = pow (0.9, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, avg+peak) * above (time, t0+.1);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %16;
index2 = (index2 + is_beat*bnot(index))%4;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass + mid + treb;
q29 = 2 * (index2%2-.5);
q25 = min(1,q22/2);

k1 =  is_beat*equal(index%4,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.1416/4;
q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;


q27 = index;
q28 = index2;

mov = mov*dec_med + (1-dec_med)*above(index%8,2);
movez = movez + 1/fps*mov;
q30 = movez;
//**************************

q8 = movex; q9 = movey;

trel1 = trel1 + (q1*q22*3)/fps;
trel2 = trel2 + (q2*q22*2)/fps;
trel3 = trel3 + 4*(1-mov)/fps;
trel4 = trel4 + 1 * mov/fps;

q11 = .3*sin(trel1); 
q12 = 0.1*cos(trel2); 
q13 = 8 + 6*sin(trel3);

q14 = .3*sin(trel2); 
q15 = .2*cos(trel3) ; 
q16 = 6 + 3*sin(trel1);

q17 = .3*sin(trel4); 
q18 = .3*cos(trel2); 
q19 = 3 + sin(trel3);

trot = trel1/2;
trot = index2*6.28/4;
q5 = cos(trot);
q6 = sin(trot);
q7 = -q6;
q8 = q5;

warp:
shader_body
{
    // sample previous frame
    ret = texture( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret *= 0.74; //or try: ret -= 0.004;
}

comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz*2.7 + ((texture(sampler_blur2, uv).xyz * scale2) + bias2)*2.75;
    ret *= 1.069;
ret /= vec3(2.1,1.7,1.7);
//MilkDrop3 RGB Mode:
ret += vec3(0.0,0.0,0.0);
//MilkDrop3 Brightness Mode:
ret *= 3.0;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret *= 0.74; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = GetPixel(uv)*2.7 + GetBlur2(uv)*2.75;
    ret *= 1.069;
ret /= float3(2.1,1.7,1.7);
//MilkDrop3 RGB Mode:
ret += float3(0.0,0.0,0.0);
//MilkDrop3 Brightness Mode:
ret *= 3.0;
}
