warp:
shader_body
{
    // sample previous frame
    ret =texture( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret *= 0.94; //or try: ret -= 0.004;
ret -=0.001;
}

comp:
shader_body
{
    ret =texture(sampler_main, uv).xyz*2 + ((texture(sampler_blur2, uv).xyz * scale2) + bias2)*3;
    //ret *= 2;
ret /= vec3(2.1,1.7,1.7);
//MilkDrop3 RGB Mode:
ret += vec3(0.0,0.0,0.0);
//MilkDrop3 Brightness Mode:
ret *= 3.0;
ret -=0.1;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret *= 0.94; //or try: ret -= 0.004;
ret -=0.001;
}

comp_hlsl:
shader_body
{
    ret = GetPixel(uv)*2 + GetBlur2(uv)*3;
    //ret *= 2;
ret /= float3(2.1,1.7,1.7);
//MilkDrop3 RGB Mode:
ret += float3(0.0,0.0,0.0);
//MilkDrop3 Brightness Mode:
ret *= 3.0;
ret -=0.1;
}
