warp:
shader_body
{
    ret = texture( sampler_main, uv ).xyz;

    // ERROR DIFFUSION DITHER - looks great
    vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (texture(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*3;

    // darken over time
    //ret -= 0.004;
    //ret *= 0.99;
    //ret *= 0.97 + 0.03*clamp( length(uv - uv_orig)*200 , 0.0, 1.0);  // center only
    ret = fract(ret - 0.005);
}

comp:
uniform sampler2D sampler_MilkDrop3_002b;  // loads clouds.jpg (or .tga, etc)

shader_body         
{
    ret = texture(sampler_main, uv).xyz +
              ((texture(sampler_blur2, uv).xyz * scale2) + bias2)
          ;

    ret = texture( sampler_MilkDrop3_002b, 
                     ret.xy*(0.1 + 0.7*rand_preset.x*rand_preset.y) 
                     + uv*vec2(1,-1)*aspect.xy
                   ).xyz;

    //ret *= 1.333; // a little bit of overbright

}

