waves.frame_eqs_eel:
vol = bass*8 + mid*5 + treb*3;
m = m*0.97 + vol*0.08;
monitor = vol;
beat = above(vol,res)*above(vol,m)*above(vol,16);
diff = (1-beat)*diff + beat*(vol-res);
res = beat*(vol + m*0.04) + (1-beat)*(res -  (0.1+diff*0.02)*60/fps);
res = max(0,res);

a = beat;

frame_eqs_eel:
wave_r = 0.85 + 0.25*sin(0.437*time+1);
wave_g = 0.85 + 0.25*sin(0.544*time+2);
wave_b = 0.85 + 0.25*sin(0.751*time+3);
rot = rot + 0.010*( 0.60*sin(0.381*time) + 0.40*sin(0.579*time) );
cx = cx + 0.210*( 0.60*sin(0.374*time) + 0.40*sin(0.294*time) );
cy = cy + 0.210*( 0.60*sin(0.393*time) + 0.40*sin(0.223*time) );
dx = dx + 0.003*( 0.60*sin(0.234*time) + 0.40*sin(0.277*time) );
dy = dy + 0.003*( 0.60*sin(0.284*time) + 0.40*sin(0.247*time) );
decay = decay - 0.01*equal(frame%6,0);
dx = dx + dx_residual;
dy = dy + dy_residual;
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.96+1.3);
dx_residual = equal(bass_thresh,2.13)*0.016*sin(time*7) + (1-equal(bass_thresh,2.13))*dx_residual;
dy_residual = equal(bass_thresh,2.13)*0.012*sin(time*9) + (1-equal(bass_thresh,2.13))*dy_residual;
wave_x = wave_x - dx_residual*7;
wave_y = wave_y - dy_residual*7;
wave_mystery = time*0.03;

warp:
shader_body
{
    ret = texture( sampler_main, uv ).xyz;

    // ERROR DIFFUSION DITHER - looks great
    vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (texture(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*3;

    // darken over time
    //ret -= 0.004;
    //ret *= 0.99;
    //ret *= 0.97 + 0.03*clamp( length(uv - uv_orig)*200 , 0.0, 1.0);  // center only
    ret = fract(ret - 0.005);
}

comp:
uniform sampler2D sampler_fw_clouds;  // loads clouds.jpg (or .tga, etc)

shader_body         
{
    ret = texture(sampler_main, uv).xyz +
              ((texture(sampler_blur2, uv).xyz * scale2) + bias2)
          ;

    ret = texture( sampler_fw_clouds, 
                     ret.xy*(0.1 + 0.7*rand_preset.x*rand_preset.y) 
                     + uv*vec2(1,-1)*aspect.xy
                   ).xyz;

    //ret *= 1.333; // a little bit of overbright

}

