warp:
shader_body
{
    // sample previous frame
    ret =texture( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - ((texture(sampler_blur1, uv).xyz * scale1) + bias1))*0.14 - 0.004;
//    ret *= 0.94;

   // add noise:
   vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.1 + rand_frame.xy;
   ret += (texture(sampler_noise_lq, dither_uv).xyz-0.5)*0.014;

ret.x = (texture(sampler_main, uv_orig).xyz.x-0.00)*0.98 - 0.004;
ret.z = 0;
}

comp:
shader_body
{
    vec2 d = texsize.zw*6;
    float dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) ).y;
    float dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) ).y;
    vec2 uv_y = uv-vec2(dx,dy)*0.6;

ret =mix(vec3(1.6,0.8,0)*texture(sampler_fc_main, uv_y).x,
                              vec3(0.5,-0.2,2), ((texture(sampler_blur1, uv).xyz * scale1) + bias1).y*0.35);

}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur1(uv))*0.14 - 0.004;
//    ret *= 0.94;

   // add noise:
   float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.1 + rand_frame.xy;
   ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)*0.014;

ret.x = (GetPixel(uv_orig).x-0.00)*0.98 - 0.004;
ret.z = 0;
}

comp_hlsl:
shader_body
{
    float2 d = texsize.zw*6;
    float1 dx = ( GetBlur1(uv+float2(1,0)*d)-GetBlur1(uv-float2(1,0)*d) ).y;
    float1 dy = ( GetBlur1(uv+float2(0,1)*d)-GetBlur1(uv-float2(0,1)*d) ).y;
    float2 uv_y = uv-float2(dx,dy)*0.6;

ret = lerp(float3(1.6,0.8,0)*tex2D(sampler_fc_main, uv_y).x,
                              float3(0.5,-0.2,2), GetBlur1(uv).y*0.35);

}
