frame_eqs_eel:
warp = 0;
volume = 0.3*(bass+mid+att);
xamptarg = if(equal(frame%15,0),min(0.25*volume*bass_att,0.5),xamptarg);
xamp = xamp + 0.5*(xamptarg-xamp);
xdir = if(above(abs(xpos),xamp),-sign(xpos),if(below(abs(xspeed),0.1),2*above(xpos,0)-1,xdir));
xspeed = xspeed + xdir*xamp - xpos - xspeed*0.055*below(abs(xpos),xamp);
xpos = xpos + 0.001*xspeed;
yamptarg = if(equal(frame%15,0),min(0.15*volume*treb_att,0.5),yamptarg);
yamp = yamp + 0.5*(yamptarg-yamp);
ydir = if(above(abs(ypos),yamp),-sign(ypos),if(below(abs(yspeed),0.1),2*above(ypos,0)-1,ydir));
yspeed = yspeed + ydir*yamp - ypos - yspeed*0.055*below(abs(ypos),yamp);
ypos = ypos + 0.001*yspeed;
beatrate = equal(beatrate,0) + (1-equal(beatrate,0))*(below(volume,0.01) + (1-below(volume,0.01))*beatrate);
lastbeat = lastbeat + equal(lastbeat,0)*time;
meanbass_att = 0.1*(meanbass_att*9 + bass_att);
peakbass_att = max(bass_att,peakbass_att);
beat = above(volume,0.8)*below(peakbass_att - bass_att, 0.05*peakbass_att)*above(time - lastbeat, 0.1 + 0.5*(beatrate - 0.1));
beatrate = max(if(beat,if(below(time-lastbeat,2*beatrate),0.1*(beatrate*9 + time - lastbeat),beatrate),beatrate),0.1);
peakbass_att = beat*bass_att + (1-beat)*peakbass_att*(above(time - lastbeat, 2*beatrate)*0.95 + (1-above(time - lastbeat, 2*beatrate))*0.995);
lastbeat = beat*time + (1-beat)*lastbeat;
peakbass_att = max(peakbass_att,1.1*meanbass_att);
//wave_x = xpos + 0.5;
//wave_y = 1-(ypos + 0.5);
wave_r = 0.5 + 0.499*( 0.60*sin(0.980*time) + 0.40*sin(1.047*time) );
wave_g = 0.5 + 0.499*( 0.60*sin(0.835*time) + 0.40*sin(1.081*time) );
wave_b = 0.5 + 0.499*( 0.60*sin(0.814*time) + 0.40*sin(1.011*time) );
wave_mystery = -0.17 + 0.03*(0.6*sin(0.637*time) + 0.4*sin(0.949*time));
mv_r = if(beat, 1, ib_r);
mv_b = if(beat, wave_b, ib_b);
//mv_a = if(beat, 0.1, ib_a);
//ib_a = 0.015;
q3 = wave_mystery;
q1 = wave_x;
q2 = 1-wave_y;
q2 = ypos+0.5;
warp=0;

//q2 = 1-(ypos + 0.5);
//q1 = 0.5;
//q2=0.5;
ob_r = 1-wave_g;
ob_b = 1-wave_r;
ob_g = 1-wave_b;

monitor = wave_y;
movement =movement + 0.4*(((bass+bass_att + 0.1*pow((bass+0.6*bass_att+0.2*treb_att),3)))/fps);
movement = if(above(movement,10000), 0, movement);
rot =1*sin(movement);
cx = wave_x;
cy = y_pos+0.5;

q8 = movement;

pixel_eqs_eel:
myy = x-q1;
myx = y-q2+0.1;
dx = 3*sin(q8*0.675)*(2*myx*myy);
dy = 3*sin(q8*0.675)*((myx*myx) - (myy*myy));

warp:
shader_body
{
vec2 uv_m = 1.0 - abs( fract( uv * 0.5 ) * 2.0 - 1.0 ); // Eo.S' mirror code
ret.x = texture( sampler_fc_main, uv_m ).z; // first julia fractal iteration
ret.y = mix(mix(texture( sampler_fc_main, uv_m ).x, ret.x,0.5), 
             texture(sampler_main, uv_orig).xyz.y,
             0.75); // 75% of motion blur to the green channel


vec2 d = texsize.zw*6;
vec2 uv_z = (uv_orig-0.5)*0.998 + 0.5; // a small dose of zoom-in
vec3 dx = ( ((texture(sampler_blur1, uv_z + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv_z-vec2(1,0)*d).xyz * scale1) + bias1) );
vec3 dy = ( ((texture(sampler_blur1, uv_z + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv_z-vec2(0,1)*d).xyz * scale1) + bias1) );
uv_z -=vec2(dx.z,dy.z)*texsize.zw*2; //shortening of the nebulous matter
ret.z = max((tex2d(sampler_fc_main,uv_m).x-0.5)*3*clamp(1-length(uv_orig-0.5)*3.2, 0.0, 1.0), // feeded by the inner of the red channel
             tex2d(sampler_fc_main,uv_z).z);
ret.z += (tex2d(sampler_fc_main,uv_z).z - ((texture(sampler_blur1, clamp(uv_z, 0.0, 1.0)).xyz * scale1) + bias1).z)*0.02; // sort of geiss' skin dot code
}

comp:
vec2 d;
vec3 dx, dy;

shader_body
{
d = texsize.zw*4;
dx = ((texture(sampler_blur1, uv_orig + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv_orig - vec2(1,0)*d).xyz * scale1) + bias1);
dy = ((texture(sampler_blur1, uv_orig + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv_orig - vec2(0,1)*d).xyz * scale1) + bias1);

float z = texture(sampler_main, uv).xyz.x*(1-texture(sampler_main, uv).xyz.z);
float y = (1-texture(sampler_main, uv).xyz.y)*(1-texture(sampler_main, uv).xyz.x);
q7 = time*200;
 vec2 bg = (uv-0.5+vec2(dx.x,dy.x)*0.15)*texsize.xy;
 float bg1 = clamp((abs(bg.x)+abs(bg.y)+q7)%64-32, 0.0, 1.0);
 float bg2 = clamp((-abs(bg.x)-abs(bg.y)+q7)%64-32, 0.0, 1.0);
 float fnord;
 if(length(bg)> 0.25*texsize.y){
  fnord = bg2;
 }else{
  fnord = bg1;
 }


ret =mix(fnord,vec3(1,0,0),z);
ret =mix(ret,0,y);
}

