warp:
shader_body
{
uv = mix(uv_orig,uv,0.3);

vec2 d = texsize.zw * 3;
vec3 dx = ( ((texture(sampler_blur1, uv + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
vec3 dy = ( ((texture(sampler_blur1, uv + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

uv += vec2(lum(dx),lum(dy))*texsize.zw*9;
ret = tex2d(sampler_fc_main, uv) - 0.01;

}

comp:
shader_body
{

uv = 0.5 + (uv-0.5)*0.98;
    vec2 d = texsize.zw*4;
    vec3 dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    vec3 dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

vec4 noise = texture(sampler_noisevol_hq, ((uv.xyy+vec3(lum(dx),lum(dy),0)*0.5)*texsize.xyy*texsize_noisevol_hq.zww).xyz*vec3(1,1,0)*0.05
                                        + time*vec3(0,0,1)*0.2 ).xyz;
uv -= vec2(lum(dx),lum(dy))*0.04;
ret = abs(texture(sampler_main, uv).xyz+((texture(sampler_blur1, uv).xyz * scale1) + bias1)-((texture(sampler_blur2, uv).xyz * scale2) + bias2)-((texture(sampler_blur3, uv).xyz * scale3) + bias3));
ret *= lum(noise)*3; 
ret = pow(ret,0.5);
}

warp_hlsl:
shader_body
{
uv = lerp(uv_orig,uv,0.3);

float2 d = texsize.zw * 3;
float3 dx = ( GetBlur1(uv + float2(1,0)*d) - GetBlur1(uv-float2(1,0)*d) );
float3 dy = ( GetBlur1(uv + float2(0,1)*d) - GetBlur1(uv-float2(0,1)*d) );

uv += float2(lum(dx),lum(dy))*texsize.zw*9;
ret = tex2d(sampler_fc_main, uv) - 0.01;

}

comp_hlsl:
shader_body
{

uv = 0.5 + (uv-0.5)*0.98;
    float2 d = texsize.zw*4;
    float3 dx = ( GetBlur1(uv+float2(1,0)*d)-GetBlur1(uv-float2(1,0)*d) );
    float3 dy = ( GetBlur1(uv+float2(0,1)*d)-GetBlur1(uv-float2(0,1)*d) );

float4 noise = tex3D(sampler_noisevol_hq, ((uv.xyy+float3(lum(dx),lum(dy),0)*0.5)*texsize.xyy*texsize_noisevol_hq.zww).xyz*float3(1,1,0)*0.05
                                        + time*float3(0,0,1)*0.2 );
uv -= float2(lum(dx),lum(dy))*0.04;
ret = abs(GetPixel(uv)+GetBlur1(uv)-GetBlur2(uv)-GetBlur3(uv));
ret *= lum(noise)*3; 
ret = pow(ret,0.5);
}
