waves.point_eqs_eel:
sample = 1-sample;
xxx = xx;
yyy = yy;
xx = pow(sample,5)*t1 + 5*pow(sample,4)*(1-sample)*t1 + 10*pow(sample,3)*sqr(1-sample)*t2
+ 10*sqr(sample)*pow(1-sample,3)*t3 + 5*pow(1-sample,4)*sample*t4 + pow(1-sample,5)*t4;

yy = pow(sample,5)*t5 + 5*pow(sample,4)*(1-sample)*t5 + 10*pow(sample,3)*sqr(1-sample)*t6
+ 10*sqr(sample)*pow(1-sample,3)*t7 + 5*pow(1-sample,4)*sample*t8 + pow(1-sample,5)*t8;

d = 1/sqrt(sqr(xx-xxx)+sqr(yy-yyy));
dd = dd*0.95 + (value1);
ddd = dd*sample*(1-sample)*d;
ddd = sample*(1-sample)*sin(sample*120)*d*0.1;
x = xx + (yy-yyy)*ddd;
y = yy - (xx-xxx)*ddd;

x = 0.5 + (x-0.5)*q9;
y = 0.5 + (y-0.5)*q10;

init_eqs_eel:
n = 0;
loop (100000,megabuf(n)=0;gmegabuf(n)=0;n += 1;);

//rand chain
n = 90000; t=0; loop (5000, t+= (2+cos(n/21))/5; megabuf(n)= sin(t)*0.5; n+= 1);


fov = 1; reg05 = fov;
maxstrings = 150;
maxssize   = 37; //Pkte pro String
maxpts     = 1600;
maxshapes  = 300;
pfree = maxpts; //freie Pkte
sfree = maxstrings;  //freie Strings

recsize = 8; reg01 = recsize; //pro Punkt
dist0 =  0.8; reg02 = dist0;
pi=3.1416;


//bei 0 starten die String-Inforecords
//0 : Age
//1 : start index
//2 : Len
//3: Alive
//4,5,6: dx0,dy0,dz0

//bei 5000 starten die Punkt-records
//es folgen die ppstring Punkte; pro punkt
  //0..2: Pos. x,y,z
// ende pro punkt

//2D-Records in gmegabuf
  //0..2: Pos. x,y,z
  //3: rad
  //5: a
  //6: mask

//Shape-Records in gmegabuf, optional
  //0..2: r0, theta, phi
  //3: speed
  //4,5,6: x,y,z
  //
sPtr = 0;
r01 = rand(12);
r02 = rand(12);

frame_eqs_eel:
dt = 1/fps;
vol = sqrt (bass_att+mid_att+treb_att)/3; vol_ = vol_*0.9 + 0.1*vol; gf = 0.35*vol/maxssize;
prog += vol*30*dt; 

// Count sfree and pfree; pfree = len=0
s = 0; sfree = maxstrings; pfree = maxpts;
loop (maxstrings, ibase = s*recsize; if (megabuf(ibase+3) > 0, sfree -=1; pfree -=megabuf(ibase+2), 0); s+= 1);

s = 0; loop (maxstrings, //NEW STRING ?
  ibase = s*recsize;
  if ((megabuf(ibase+3)==0) && (vol > vol_*1) && (rand(100) < 20),
    sfree -= 1;
    tht = rand(1)*(2.9 + (vol>vol_*1.7)); phi = (rand(1)-0.5)*4+pi/2;;
    pbase = 5000 + (s*maxssize)*recsize; //erster Punkt startet bei 0
    megabuf (ibase  ) = 0;//Age
    megabuf (ibase+1) = 0;//start ind
    megabuf (ibase+2) = 0;//len  
    megabuf (ibase+3) = 1;// am Leben
    megabuf (ibase+4) = sin(tht)*cos(phi);
    megabuf (ibase+5) = sin(tht)*sin(phi);
    megabuf (ibase+6) = -abs(cos(tht)); 
  ,0); //END NEW STRING
s+=1;);

s = 0; loop (maxstrings, //GROW and SHRINK
  ibase = s*recsize;
  if (megabuf(ibase+3) > 0, //am Leben ?
    megabuf(ibase  ) += dt*rand(2) ; //Age
//EXPAND
    age   = megabuf (ibase);
    start = megabuf (ibase+1);
    len   = megabuf (ibase+2);
    newpts = int (5*vol*maxssize/40*(age < 0.7)) ; 
    newpts = max (0, min (newpts, min (pfree,maxssize-start-len))); 
    dx0 = megabuf (ibase+4);
    dy0 = megabuf (ibase+5);
    dz0 = megabuf (ibase+6);
    p = start+len;
    loop (newpts,
      pbase  = 5000 + (p+s*maxssize)*recsize;
k1 = if (s%3==0, p, (p-start-len)*16)+3+s/16;
if (frame%5==0,k1 = 20*vol,0);
//k1 = max(k1,p);
      megabuf (pbase) = k1*dx0*gf; megabuf (pbase+1) = k1*dy0*gf; megabuf (pbase+2) = k1*dz0*gf;      
      p+=1; ); 
    megabuf(ibase +2) += newpts;  len   = megabuf (ibase+2);  pfree -= newpts;
//SHRINK
    ivol = if (vol<0.35,3, if (vol<0.5,2,1));
    delpts = min ((megabuf(ibase)>.7),len) * (frame%ivol==0);
    megabuf(ibase+1) = (megabuf(ibase+1)+delpts); 
    megabuf(ibase+2) -= delpts;  pfree += delpts;
    if (megabuf(ibase+2) <= 0, megabuf(ibase+3)=0, 0);
  ,0);
s+=1);

//MOVE
s = 0; loop (maxstrings,
  ibase = s*recsize;  start = megabuf (ibase+1); len = megabuf (ibase+2);
  if (len > 0, 
    p=start; i=0;
    loop (len,
      pbase  = 5000 + (p%maxssize+s*maxssize)*recsize;
      scale = pow(i/len,2)*0.005 ;
      r1 = pow(megabuf(pbase),2)+pow(megabuf(pbase+1),2)+pow(megabuf(pbase+2),2);
      scale *= min(sqrt(r1)*10,1) ; //##mit vol skalieren
      p1 = 5000-int(3*p+prog);
      megabuf (pbase)   += megabuf(90000+(p1+s*57)%5000)*scale;      
      megabuf (pbase+1) += megabuf(90000+(p1+s*91)%5000)*scale;      
      megabuf (pbase+2) += megabuf(90000+(p1+s*113)%5000)*scale;       
      p+=1; i+=1;   ); 

//STRAIGHTEN
    p=start+1; smooth = 8;
    sz = 1/(1.93+smooth);
    loop (len-2,
      pbase  =  5000 + (p    +s*maxssize)*recsize;
      pbase1  = 5000 + ((p-1)+s*maxssize)*recsize;
      pbase2  = 5000 + ((p+1)+s*maxssize)*recsize;
      megabuf (pbase  ) = (smooth*megabuf(pbase  ) +megabuf(pbase1  )+megabuf(pbase2 ))*sz;      
      megabuf (pbase+1) = (smooth*megabuf(pbase+1) +megabuf(pbase1+1)+megabuf(pbase2+1))*sz;      
      megabuf (pbase+2) = (smooth*megabuf(pbase+2) +megabuf(pbase1+2)+megabuf(pbase2+2))*sz;      
     p+=1; ); 

  ,0);
s+=1);

//Projektion waves
n=0; loop (maxpts, gmegabuf(n+6)=0; n+=recsize); 
s = 0; i = 0;
loop (maxstrings,
  ibase = s*recsize;
  start = megabuf (ibase+1);
  len   = megabuf (ibase+2);
  p = start;  j=0;
//  if (len==0, len=-1, 0);
  loop (len+1, //ersten Pkt verdoppeln wegen waves
    pbase = 5000 + (p%maxssize + s*maxssize)*recsize;
    z    = megabuf (pbase+2)+dist0;
    gmegabuf (i*recsize)   = megabuf(pbase  );    
    gmegabuf (i*recsize+1) = megabuf(pbase+1);    
    gmegabuf (i*recsize+2) = z;    
    gmegabuf (i*recsize+3) = if (z>0, 0.007/z, 0) ;    //rad
    gmegabuf (i*recsize+5) = 1; //a
    gmegabuf (i*recsize+6) = (z>0.1)*(j>1) * (j<len-1); //mask
    if (j>1, p += 1,0);  i += 1; j+=1;
  );  
  s+=1;
);

//Vol delay chain 
tvol = tvol*0.8 + 0.2*(vol-vol_);
n = 50030; loop (30, gmegabuf (n) = gmegabuf (n-1); n -= 1; ); gmegabuf (50000) = tvol;


dec = if (vol>pvol,0.5, 1-dt*40*0.05);
pvol = pvol*dec + vol*(1-dec);
q1 = pvol;

//Zentrum
q10 = 0.5; 
q11 = 0.38;
q32 = aspecty;

q3 = gmegabuf(50000)+1; 
q4 = gmegabuf(50005)+1; 

//planet pos
t1 = time/12 +r01;
q15 = 0.5-0.35*sin(t1);
q16 = 0.5- (0.05+0.24*cos(t1) * (0.7+0.3*sin(t1/7))); 

q8 = (1.7+sin(time*.02+r02))*.16;
q9 = (1.7+sin(time*.0157-r02))*.16;

bal = min(0.85,max(0.15,bal+ dt*0.1 * atan(treb -bass - (bal-0.5)*0.1)));
q12 = bal;

monitor = q12;

warp:
shader_body
{
    vec2 d = texsize.zw * 8;
    vec3 dx = ( 2*((texture(sampler_blur1, uv + vec2(1,0)*d).xyz * scale1) + bias1) - 2*((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) )*0.5;
    vec3 dy = ( 2*((texture(sampler_blur1, uv + vec2(0,1)*d).xyz * scale1) + bias1) - 2*((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) )*0.5;
    vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 1 + rand_frame.xy;

vec2 uv_blue = mix(uv_orig,uv,1) + vec2(dx.z,dy.z)*texsize.zw*4;
ret.z = texture(sampler_main, uv_blue).xyz.z  - (2*texture(sampler_main, uv_blue).xyz.z - 2*((texture(sampler_blur3, uv_blue).xyz * scale3) + bias3).z)*0.01 - 0.008
            +(texture(sampler_noise_lq, dither_uv).xyz-0.5) *0.1;


vec2 uv_red = 0.5 + (uv-0.5) - vec2(0,0)*texsize.zw - vec2(dx.x,dy.x)*texsize.zw;
ret.x = texture( sampler_main, uv_red ).x;
ret.x += (2*ret.x - 2*((texture(sampler_blur3, uv_red).xyz * scale3) + bias3)).x*0.2 + 0.006;


vec2 uv_green = mix(uv_orig,uv,1) - vec2(dx.x,dy.x)*texsize.zw*8
                                       + vec2(dx.y,dy.y)*texsize.zw*4
                                       + vec2(dx.z,dy.z)*texsize.zw*8;
ret.y = tex2d(sampler_fc_main,uv_green).y;
ret.y *= 1 + ret.x*0.1;
ret.y -= 0.004 + clamp(ret.z, 0.0, 1.0)*0.012;
}

comp:
shader_body
{
    vec2 d = texsize.zw*4;
    vec3 dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    vec3 dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );
vec2 uv_bg = uv - vec2(dx.y,dy.y)*texsize.zw*16;
ret = (((texture(sampler_blur2, uv_bg).xyz * scale2) + bias2)-((texture(sampler_blur1, uv_bg).xyz * scale1) + bias1)*1+texture(sampler_main, uv_bg).xyz*0.9 + (-dx.y + dy.y)*0.4).y*0.8*vec3(1,1.1,1.2);

vec2 uv_m = uv - vec2(dx.x,dy.x)*texsize.zw*8;
ret = mix(ret, 2*vec3(0,0.5,1), ((texture(sampler_blur3, uv_m - vec2(dx.y,dy.y)*0.4).xyz * scale3) + bias3).x*0.4);
ret = mix(ret, 2*vec3(0.5,1,1), ((texture(sampler_blur2, uv_m - vec2(dx.y,dy.y)*0.2).xyz * scale2) + bias2).x*0.4);
ret = mix(ret, 2*vec3(1,1,0.8), ((texture(sampler_blur1, uv_m - vec2(dx.y,dy.y)*0.1).xyz * scale1) + bias1).x*0.2);
//ret = mix(ret, vec3(0.5,0,1), texture(sampler_main, uv_m).xyz.x*0.4);
}

warp_hlsl:
shader_body
{
    float2 d = texsize.zw * 8;
    float3 dx = ( 2*GetBlur1(uv + float2(1,0)*d) - 2*GetBlur1(uv-float2(1,0)*d) )*0.5;
    float3 dy = ( 2*GetBlur1(uv + float2(0,1)*d) - 2*GetBlur1(uv-float2(0,1)*d) )*0.5;
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 1 + rand_frame.xy;

float2 uv_blue = lerp(uv_orig,uv,1) + float2(dx.z,dy.z)*texsize.zw*4;
ret.z = GetPixel(uv_blue).z  - (2*GetPixel(uv_blue).z - 2*GetBlur3(uv_blue).z)*0.01 - 0.008
            +(tex2D(sampler_noise_lq, dither_uv).xyz-0.5) *0.1;


float2 uv_red = 0.5 + (uv-0.5) - float2(0,0)*texsize.zw - float2(dx.x,dy.x)*texsize.zw;
ret.x = tex2D( sampler_main, uv_red ).x;
ret.x += (2*ret.x - 2*GetBlur3(uv_red)).x*0.2 + 0.006;


float2 uv_green = lerp(uv_orig,uv,1) - float2(dx.x,dy.x)*texsize.zw*8
                                       + float2(dx.y,dy.y)*texsize.zw*4
                                       + float2(dx.z,dy.z)*texsize.zw*8;
ret.y = tex2d(sampler_fc_main,uv_green).y;
ret.y *= 1 + ret.x*0.1;
ret.y -= 0.004 + saturate(ret.z)*0.012;
}

comp_hlsl:
shader_body
{
    float2 d = texsize.zw*4;
    float3 dx = ( GetBlur1(uv+float2(1,0)*d)-GetBlur1(uv-float2(1,0)*d) );
    float3 dy = ( GetBlur1(uv+float2(0,1)*d)-GetBlur1(uv-float2(0,1)*d) );
float2 uv_bg = uv - float2(dx.y,dy.y)*texsize.zw*16;
ret = (GetBlur2(uv_bg)-GetBlur1(uv_bg)*1+GetPixel(uv_bg)*0.9 + (-dx.y + dy.y)*0.4).y*0.8*float3(1,1.1,1.2);

float2 uv_m = uv - float2(dx.x,dy.x)*texsize.zw*8;
ret = lerp(ret, 2*float3(0,0.5,1), GetBlur3(uv_m - float2(dx.y,dy.y)*0.4).x*0.4);
ret = lerp(ret, 2*float3(0.5,1,1), GetBlur2(uv_m - float2(dx.y,dy.y)*0.2).x*0.4);
ret = lerp(ret, 2*float3(1,1,0.8), GetBlur1(uv_m - float2(dx.y,dy.y)*0.1).x*0.2);
//ret = lerp(ret, float3(0.5,0,1), GetPixel(uv_m).x*0.4);
}
