frame_eqs_eel:
vol = bass + mid + treb;
vol_ = .9*vol_+.1*vol;


k1 = above (vol, vol_*1.3);
k2 = k2*.9 + 0.1*k1;
k3 = k3*.9 + 0.1*k2;

q10 = k2;
q11 = k3;
q12 = vol_;

dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.99, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .5+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %8;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass + mid + treb;

k1 =  is_beat*equal(index,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.1416/2;
q27 = index + 1;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

trel = time/8;
q5 = cos(trel);
q6 = sin(trel);
q7 = -q6;
q8 = q5;
q9 = sin(time/13)+2;


q32 = pow(0.99, 30/fps); //fade

pixel_eqs_eel:
p1 = sin (time/7);
p2 = cos (time/3);
p3 = 1+(cos (time/6));
p4 = .5*cos (time/4.5);

dx  = 0.004*p1*rad;
dy = 0.004*p2*rad ;

zoom = 1 + pow(rad,p4)*p3/50;
rot = 0;

