warp:
shader_body
{
uv = uv.xy - 0.5;
//uv.x *= aspect_ratio;

float dist = length(uv) * 3;
float angle = atan(uv.y, uv.x);
float t = time * 0.75;

vec3 color = 0;
for(int i=0; i<3; i++) {
    float fi = float(i)*0.3;
    float wave = sin(dist * 4 - t*4 + fi) * 0.5;
    wave += sin(angle*3 + t*2 + fi) * 0.3;
    wave += sin((dist*2 + angle*2) - t*3 + fi) * 0.2;

    color[i] = 0.7 / (0.1 + abs(wave)) * (1 - dist*0.3);
}

color = mix(color, color.gbr, sin(t)*0.3 + 0.3);
color *= .1 + vol/22;

ret = vec4(color * (0.8 + 0.3*sin(t + uv.x*5)),1);
}

comp:
shader_body
{

    vec2 uv_echo = (uv - 0.5)*1.000*vec2(-1,-1) + 0.5;
    ret = mix( texture(sampler_main, uv).xyz, 
                texture(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    ret *= 2.00; //gamma
    vec3 retish = 1 - ret*(1-ret); //solarize
    ret = ((texture(sampler_blur3, uv).xyz * scale3) + bias3) + ((texture(sampler_blur3, uv_echo).xyz * scale3) + bias3) + ret*2;
//MilkDrop3 Shadows Mode:
ret += pow(texture(sampler_main,vec2(uv.x,1-uv.y)).yyy+texture(sampler_main,1-uv).yyy,vec3(1,2,3));
ret-=0.2;
//MilkDrop3 Color Mode:
ret *= vec3(0.4,0.5,0.9);
}

