warp:
shader_body
{
uv = uv.xy - 0.5;
//uv.x *= aspect_ratio;

float dist = length(uv) * 3;
float angle = atan(uv.y, uv.x);
float t = time * 0.75;

vec3 color = vec3(0);
for(int i=0; i<3; i++) {
    float fi = float(i)*0.3;
    float wave = sin(dist * 4 - t*4 + fi) * 0.5;
    wave += sin(angle*3 + t*2 + fi) * 0.3;
    wave += sin((dist*2 + angle*2) - t*3 + fi) * 0.2;

    color[i] = 0.7 / (0.1 + abs(wave)) * (1 - dist*0.3);
}

color = mix(color, color.gbr, sin(t)*0.3 + 0.3);
color *= .1 + vol/22;

ret =vec4(color * (0.8 + 0.3*sin(t + uv.x*5)),1).xyz;
}

