warp:
// by LamersAss, V1 (28/5/2022)

shader_body
{
  vec3 _fx_vol = vec3( bass_att, mid_att, treb_att )/3;

  for ( int i = 0; i < 3; i++ )
  {
    _fx_vol[i] = ( min( 1., _fx_vol[i] ) * 1.7 )
                 + max( .0, _fx_vol[i] - 1. );
    _fx_vol[i] = min( 2.46, _fx_vol[i] );
  }

  _fx_vol += rot_f3[0] * .5;
  _fx_vol *= .07;
  _fx_vol *= 1.9;
  _fx_vol += 0.022;

  float _fx_decay = fps * 0.013786;
  float _fx_rad = length( vec2( 
    ( uv.x - .5 ) * aspect.x, 
    ( uv.y - .5 ) * aspect.y ) );
  float _fx_zoom = max( 0, _fx_rad - .12 ) * .028;

  _fx_zoom *= 1. + max( 0, ( cos( ang * 4 ) * .7 ) - .4 );

  vec2 _fx_prevframe = uv_orig;
  _fx_prevframe.x += ( _fx_zoom * aspect.y ) * ( uv_orig.x > .5 ? -1. : 1. );
  _fx_prevframe.y += ( _fx_zoom * aspect.x ) * ( uv_orig.y > .5 ? -1. : 1. );

  ret = texture( sampler_main, _fx_prevframe ).xyz * _fx_decay;

  float _fx_width = ( _fx_rad * .04 ) + ( vol * .004 );
  float _fx_spinmult = 600.;

  for ( int i = 0; i < 3; i++ )
    if ( _fx_rad > ( _fx_vol[i] - _fx_width ) 
      && _fx_rad < ( _fx_vol[i] + _fx_width ) )
      ret[i] = max( ret[i],
        ( 12. * _fx_vol[i] ) *
        + ( 12. * cos( ang + ( _fx_spinmult * _fx_vol[i] ) ) )
     );

  vec3 _fx_glow_intens = vec3(1.7,0.3,2.5);
  vec3 _fx_glow_vol = ( _fx_vol * 0.92 ) * max( 0, _fx_rad - .2 );

  for ( int i = 0; i < 3; i++ )
    ret[i] = max( ret[i], _fx_glow_vol[i] * _fx_glow_intens[i] );
}

comp:
float a,b,c;
shader_body
{
//uv = 0.25 + 0.5*uv; //MilkDrop2077 zoomed effect

vec2 uv2 = uv;
if(uv2.x >= 0.5) {uv2.x = 1-uv2.x;}
if(uv2.y >= 0.5) {uv2.y = 1-uv2.y;}

uv.x=uv2.x;
uv.y=uv2.y;


vec3 base = ((texture(sampler_blur3, uv).xyz * scale3) + bias3) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2) + ((texture(sampler_blur1, uv).xyz * scale1) + bias1) + texture(sampler_main, uv).xyz*1.0747 - 0.55 ;
a =base.x/10;b = base.y/8; c = base.x/3;
c = c + b + a-b;
a = a + c + a*c;
base = .3+c;
ret = base*(0.552-base)*(2-base)*2*vec3(0.3,2.3,0.5) + pow(base,2)*vec3(2.6,0.6,0.8); //MilkDrop2077 color mod
//ret.xy = ret.yx;

ret *= vec3(uv.x*4,-1,uv.y*10);
//ret = ret*(1-ret+)*3; //MilkDrop2077 solarize effect
//ret -= 0.1;
}

