init_eqs_eel:
x0 = .25 + rand(10)/10;
y0 = .25 + rand(10)/10;
vx0 = .25 - rand(10)/20;
vy0 = .25 - rand(10)/20;

x1 = .25 + rand(10)/50;
y1 = .25 + rand(10)/50;
vx1 = .25 - rand(10)/20;
vy1 = .25 - rand(10)/20;

//# average of positions should be at (.5, .5)
x2 = 1.5 - x0 - x1;
y2 = 1.5 - y0 - y1;

//# average of velocities should be 0
vx2 = -vx0 - vx1;
vy2 = -vy0 - vy1;

warp:
#define tex sampler_Pattern_032
sampler tex;

shader_body
{

vec2 xy1=vec2(0);
vec2 xymod=uv-.5;

xy1.x=sin(time/3)*xymod.x-cos(time/3)*xymod.y;
xy1.y=cos(time/3)*xymod.x+sin(time/3)*xymod.y;

uv=xy1*7+aspect.xy;


    float t = clamp((0.9)*3, 0.0, 1.0);
    uv = mix(uv_orig, uv*3, t*0.1);

    vec2 v1 = normalize(uv_orig-0.5);
    vec2 v2 = v1.yx * vec2(1,-1);

    float z = length(texsize.zw)*150;

