comp:
//uniform sampler2D sampler_rand00;

shader_body {
float cutoff = 100;

vec2 d1 = uv - vec2(q1,q2);
vec2 d2 = uv - vec2(q3,q4);
vec2 d3 = uv - vec2(q5,q6);
vec2 d4 = uv - vec2(q1,q8);

// dot(d1,d1) is in 0...3
// pot1 is in .33 ... 100
// at distance 0, pot1 is 100
// at distance .1, pot1 is 50
// at distance .05, pot1 is 1/(.0125) = 80
// at distance .11, pot1 is 1/(.0221) = 45
// at distance .2, pot is 1/.05 = 20;
// at distance 1, pot is 1/1.01 ~= 1;
float pot1 = 1/(dot(d2,d1) + .01);
float pot2 = 1/(dot(d3,d2) + .01);
float pot3 = 1/(dot(d3,d3) + .01);
float pot4 = 3/(dot(d1,d4) + .01);
//          |stacked | center | float  | edge  |      | float | far away
// ------------------| -------------------------------------------------
// distance:| 0      | 0      | .05   | .1    | .11  | .2     | 1
// pot:     | 300    | 100    | 80    | 50    | 45   | 20     | 1
// z:       | > .1   | .1     | Ã¢ÂÂ2/2  |  0    | imaginary
//

float pot = pot1 + pot2 + pot3 + pot4;

// pot is between 0 and 300
// when distance is 0 from one and far from the others, pot is 100, z should be .1
// on boundary, pot is 50, z should be 0

vec2 texloc = uv;
if (pot > 50) {
float z = (sqrt(pot - 50))/20;

float wt1 = pot1/pot;
float wt2 = pot2/pot;
float wt3 = pot3/pot;
float wt4 = pot4/pot;

vec3 d1z = normalize(vec3(d1.x, d1.y, z));
vec3 d2z = normalize(vec3(d2.x, d2.y, z));
vec3 d3z = normalize(vec3(d3.x, d3.y, z));
vec3 d4z = normalize(vec3(d4.x, d4.y, z));

vec3 n = normalize(d1z*wt1 + d2z*wt2 + d3z*wt3 + d4z*wt4);

texloc = uv + .05*n.xy/n.z;
}
ret =texture(sampler_fw_main, texloc).xyz;
}

