frame_eqs_eel:
dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.96, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, 0.2+avg+peak) * above (time, t0+.1);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %12;
index2 = (index2 + is_beat*bnot(index))%8;

q20 = avg;
q21 = beat;
q22 = peak;
q24 = is_beat;
vol = (bass_att + mid_att + treb_att)/2;
vol_ = dec_med * vol_ + (1-dec_med)* vol;
vol__ = dec_med * vol__ + (1-dec_med)* vol_;
q27 = index + 1;
q28 = index2 + 2;

k1 =  is_beat*equal(index%4,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.1416/4;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

trig = q24 * bnot(index%8);
vx = vx*bnot(trig) + trig* (int(rand(100))/100-.5);
vy = vy*bnot(trig) + trig* (int(rand(100))/100-.5);
vz = vz*bnot(trig) + trig* (int(rand(100))/100-.5);

speed = min(vol_-vol__+.5,1);
speed = max(speed,-.5);
speed_ = bnot(trig)*speed_ + trig*speed;
spg = dec_med*spg + (1-dec_med)*speed_;
movz = movz + 1/fps*(speed_+0*vol_/6);
q9 = movz;
q12 = min(q22,6)*2;

//puls = puls*dec_med + (1-dec_med)*q22;

q13 = 2* sin(movz/3-time/11);

trig = bnot(index%4) * q24;
dir = bnot(trig)*dir + trig*(int(rand(10))-5);
trel = trel + .1/fps*dir;
q5 = cos(trel-movz);
q6 = sin(trel/2-movz);
q7 = -q6;
q8 = q5;

ang0 = ang0*dec_med + vz*(1-dec_med);
q17 = ang0*2; 
q30 = .2*sin(time/8);

q31 = .5 + .5*sin(time/3.3);

zoom = 1;
rot = .0;
dx = .00;
monitor = q28;

warp:
uniform sampler2D sampler_worms;
vec3 color, mus;
float dx,dy;
shader_body {


vec2 uv1 = (uv_orig-.5) * aspect.xy;

vec2 uv6 = uv1;

float z = 6*q2*length(uv1) ;
vec2 d = -normalize(1/uv1);

vec2 rs = clamp(tan(q1*z+q27/8)*d,-8,8);

//rs *= cos(uv1.yx*q27*3) ;

uv += rs/32;

uv6 = 2*(vec2(rad, length(uv1)));

//uv6 = (mat2(_qa) * uv6);

mus = (1/(sqrt(uv6.y-q27/18)));
//mus = 1/(length(.1*sin(22*uv+rand_frame*6)));
//mus *= vec3 (1,.84,.5);
//mus *= roam_cos;

vec3 blur = ((texture(sampler_blur1, fract(uv)).xyz * scale1) + bias1);

vec3 crisp= texture(sampler_main,uv).xyz - .03*rad;

vec3 ret1 = crisp - blur*.0 + .02*mus* roam_cos;
ret = q24*(ret1 * .99 -  .0206) + (1-q24)*texture(sampler_main, (uv_orig-.5)*1+.5).xyz;


}

comp:
vec3 ret1, neu;
vec2 rs,rs2,uv1;
shader_body
{
vec2 uv2;
float ang2, c, s;
uv -= 0.5;
uv *= aspect.xy;    
//uv = (mat2(_qa) * uv);

vec2 tmp = uv;
float dist = 1;
float dista = 0;
float inten = 1;
float mask = 0;
ret1 = 0;
int anz = 4;
float n = 0;
while (n <= anz) {
    ang2 = 6.28*n/anz;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;
uv2 *= aspect.yx;
    dist = 1-fract(.25*n+q30); //evtl sqrt !
;
    inten =  sqrt(dist)*(1-dist)*4;
    vec2 uv3 =  fract(3*uv2*dist+.3*q27);
    uv3 += q17*vec2 (c,s);
    neu = 1*texture(sampler_main, uv3).xyz -((texture(sampler_blur2, uv3*1.02*q1).xyz * scale2) + bias2);
    neu *= (neu >= 0);
    mask = 1-(neu*inten >= .1) * (dist < dista);
    ret1 = max(ret1,neu*inten);
    dista = dist;
n++;
}

rs2 = .4*cos(uv*13+time) - 1* lum(ret1);;
float dots = (.05/length(rs2))*q26/4 ;

ret = 1*ret1 + dots*hue_shader ;

}

