comp:
vec2 complex_div(vec2 numerator, vec2 denominator){
   return vec2( numerator.x*denominator.x + numerator.y*denominator.y,
                  numerator.y*denominator.x - numerator.x*denominator.y)/
          (denominator.x*denominator.x + denominator.y*denominator.y);
}

vec2 uv_polar(vec2 domain, vec2 center){
   vec2 c = domain - center;
   float rad_hq = length(c);
   float ang_hq = atan(c.x,c.y);
   return vec2(ang_hq, rad_hq);
}

vec2 uv_polar_logarithmic(vec2 domain, vec2 center, int fins, float log_factor, vec2 coord){
   vec2 polar = uv_polar(domain, center);
   return vec2(polar.x*fins+coord.x, log_factor*log(1/polar.y) + coord.y);
}

vec2 uv_moebius_transformation(vec2 domain, vec2 zeroPoint, vec2 infinityPoint,float zoom){
   return complex_div((domain - zeroPoint)*zoom, domain - infinityPoint)+0.5;
}

vec2 uv_bipolar_logarithmic(vec2 domain, vec2 northPole, vec2 southPole, int fins, float log_factor, vec2 coord){
   vec2 help_uv = uv_moebius_transformation(domain, northPole, southPole, 1);
   return uv_polar_logarithmic(help_uv,0.5,fins,log_factor,coord)*vec2(M_INV_PI_2,1);
}

vec2 uv_scale(vec2 domain, vec2 center, vec2 scale){
   return center + (domain-center)*scale;
}
vec2 uv_lens_half_sphere(vec2 domain, vec2 position, float radius, float refractivity){
   vec2 polar = uv_polar(domain, position);
   float cone = clamp(1-polar.y/radius, 0.0, 1.0);
   float halfsphere = sqrt(1-pow(cone-1,2));
   float w = atan(1-cone,halfsphere);
   float refrac_w = w-asin(sin(w)/refractivity);
   float refrac_d = 1-cone - sin(refrac_w)*halfsphere/cos(refrac_w);
   vec2 refrac_uv =position+vec2(sin(polar.x),cos(polar.x))*refrac_d*radius;
   bool mask =(length(domain-position)<radius);
   return !mask*domain + 
           mask*refrac_uv;
}
