comp:
float z;
vec3 ret1, neu, mus;
vec2 rs,rs2,uv1,uv2, uv3,uv4,uv6,dz,dz1;
shader_body
{
vec2 uv2;
//uv = (uv-0.5) * aspect.xy + .5;    
dz = 0;


vec2 hor = vec2(texsize.z,0);
vec2 ver = vec2(0,texsize.w);

float dist = 1;
float inten = 1;
ret1 = 0;
float anz = 1;
float n = 0;
uv4 = (uv);
    dz.x = lum(texture(sampler_main, uv4+hor).xyz- texture(sampler_main, uv4-hor).xyz);
    dz.y = lum(texture(sampler_main, uv4+ver).xyz- texture(sampler_main, uv4-ver).xyz);

//uv4 = 0;

mus = .5*texture(sampler_main, uv4+.1*dz).xyz+0*dz.x+.01;

uv2 = uv-.5;
while (n <= anz) {
    dist = 1-fract(1.0/anz*n-.1); //evtl sqrt !;

    inten =  sqrt(dist)*(1-dist*dist)*4;

    uv3 = 2*uv2*dist + .5 + 0.0*time;

//    uv3 *= aspect.yx;
    uv3 = fract(uv3);

    uv3 = .2 * cos(42*uv3+time/2) + 99*dz;
    neu = clamp(.01/length(uv3), 0.0, 1.0) ;
    neu = dist*neu + (1-dist)*lum(neu);
    ret1 = max(ret1,neu*inten);
n++;
}


ret =ret1  +  clamp(16*mus*(.1+ret1), 0.0, 1.0);
}

