init_eqs_eel:
mv_x=64;mv_y=48;
nut=0;
stp=0;stq=0;
rtp=0;rtq=0;
wvr=0;
decay=0;
dcsp=0

frame_eqs_eel:
decay=0.98;
zoom=1.002;

vol=(bass_att+mid_att+treb_att)*0.20;
vol=vol*vol;
mtime=mtime+vol*0.1*(75/fps);

q1=mtime*0.4;

warp:
shader_body
{
    // sample previous frame
    ret = texture( sampler_main, uv ).xyz*.85;

    // darken over time
    ret -= 0.022;
}

comp:
shader_body
{
    vec3 txr = vec3(uv,q2);
    vec2 uv_echo = (uv - 0.5)*vec2(-1,1) + 0.5;
    ret = mix( texture(sampler_main, (uv-0.5)*0.98 + 0.5).xyz, 
                texture(sampler_main, (uv_echo-0.5)*0.98 + 0.5).xyz, 
                0.50 
              )  + ((texture(sampler_blur3, uv).xyz * scale3) + bias3) + ((texture(sampler_blur3, uv_echo).xyz * scale3) + bias3)
              + ((texture(sampler_blur1, uv).xyz * scale1) + bias1) + ((texture(sampler_blur1, uv_echo).xyz * scale1) + bias1); //video echo; //video echo
    ret *= texture(sampler_noisevol_hq, txr).xyz; 
   ret *= 2.0; //gamma
    //ret =  ret*(1 - ret)*10; //brighten
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz*.85;

    // darken over time
    ret -= 0.022;
}

comp_hlsl:
shader_body
{
    float3 txr = float3(uv,q2);
    float2 uv_echo = (uv - 0.5)*float2(-1,1) + 0.5;
    ret = lerp( tex2D(sampler_main, (uv-0.5)*0.98 + 0.5).xyz, 
                tex2D(sampler_main, (uv_echo-0.5)*0.98 + 0.5).xyz, 
                0.50 
              )  + GetBlur3(uv) + GetBlur3(uv_echo)
              + GetBlur1(uv) + GetBlur1(uv_echo); //video echo; //video echo
    ret *= tex3D(sampler_noisevol_hq, txr); 
   ret *= 2.0; //gamma
    //ret =  ret*(1 - ret)*10; //brighten
}
