warp:
shader_body
{
    vec2 d = texsize.zw * 8;
    vec3 dx = ( ((texture(sampler_blur1, uv + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    vec3 dy = ( ((texture(sampler_blur1, uv + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );
    vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 1 + rand_frame.xy;

vec2 uv_blue = mix(uv_orig,uv,1) + vec2(dx.z,dy.z)*texsize.zw*04;
ret.z = texture(sampler_main, uv_blue).xyz.z  - (texture(sampler_main, uv_blue).xyz.z - ((texture(sampler_blur3, uv_blue).xyz * scale3) + bias3).z)*0.02 - 0.008
            +(texture(sampler_noise_lq, dither_uv).xyz-0.5) *0.1;


vec2 uv_red = 0.5 + (uv-0.5) - vec2(0,0)*texsize.zw - vec2(dx.x,dy.x)*texsize.zw;
ret.x = texture( sampler_main, uv_red ).x;
ret.x += (ret.x - ((texture(sampler_blur3, uv_red).xyz * scale3) + bias3)).x*0.23 + 0.006;


vec2 uv_green = mix(uv_orig,uv,1) - vec2(dx.x,dy.x)*texsize.zw*8
                                       + vec2(dx.y,dy.y)*texsize.zw*4
                                       + vec2(dx.z,dy.z)*texsize.zw*8;
ret.y = tex2d(sampler_fc_main,uv_green).y;
ret.y *= 1 + ret.x*0.1;
ret.y -= 0.004 + clamp(ret.z, 0.0, 1.0)*0.012;
}

comp:
shader_body
{

vec2 uv1 = uv-.5;

vec3 ret1 = texture(sampler_main, uv).xyz +texture(sampler_main, 1-uv).xyz*1;


vec2 hor = vec2 (texsize.z,0);
vec2 ver = vec2 (0,texsize.w);
float dx = texture(sampler_main, uv-hor).xyz - texture(sampler_main, uv+hor).xyz;
float dy = texture(sampler_main, uv-ver).xyz - texture(sampler_main, uv+ver).xyz;
vec2 dz = vec2 (dx,dy);

uv1 = .35*cos(uv1*bass_att*2) -6*dz;;
vec3 dots = clamp(.03/length(uv1), 0.0, 1.0);


ret1 = (-ret1/4) + 6*dots*(-0.08+ret1);

ret = lum(ret1)*0.4;
ret = lum(ret); //MilkDrop2077 monochrome effect
//MilkDrop3 Color Mode:
ret /= vec3(0.7,1.0,1.2);
//MilkDrop3 Red Dot:
ret /= vec3(rad*3,1,1);
//MilkDrop3 Brightness Mode:
ret *= 2.2;
}

warp_hlsl:
