init_eqs_eel:
mv_x=64;mv_y=48;
nut=0;
stp=0;stq=0;
rtp=0;rtq=0;
wvr=0;
decay=0;
dcsp=0;
q1=0;q2=0;q3=0

frame_eqs_eel:
decay=1;
zoom=1.010;
speed=0.60;
speedinv=1-speed;
q1=(qa*speed + bass*speedinv);
q2=(qb*speed + mid *speedinv);
q3=(qc*speed + treb*speedinv);
qa=q1;
qb=q2;
qc=q3;
flux=sin(time/2);
q4=flux * 0.5 + 0.5;
q5=flux;

q8=treb_att;
q9=mid_att;

vol = 0.33*(treb_att + mid_att + bass_att);
q10=vol;
r = treb_att;

pixel_eqs_eel:
wave_a = (treb_att + mid_att);

wave_x = wave_x - (mid_att / 120)*sin(time*0.005);
wave_y = wave_y - (treb_att / 125)*sin(time*0.009);
g = treb_att;

warp:
shader_body
{
    vec4 N = texture(sampler_noise_lq, rand_frame.xy + uv*
                             texsize_noise_lq.zw*texsize.xy ).xyz*2-1;
    // sample previous frame
    ret = texture( sampler_pw_main, uv + N.zw*texsize.zw*0.5 ).xyz;
    ret += (0.59-0.9*rad)*N.xyz;


    ret = clamp((ret-0.5)*3 + 0.25, 0.0, 1.0);
    ret.yz = ret.x;
}

comp:
uniform sampler2D sampler_MilkDrop3_017;
uniform sampler2D sampler_MilkDrop3_016;

shader_body
{


    vec2 d = texsize.zw*12;
    vec3 dx = ( ((texture(sampler_blur1, uv + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    vec3 dy = ( ((texture(sampler_blur1, uv + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );
    vec2 my_uv = uv + vec2(dx.y,dy.y)*0.1;

vec3 re;

//uv = uv + 0.02*vec2(cos(uv.y*17),sin(uv.x*39));
vec2 m = vec2(1-q4,q8);
vec2 bg_uv = m + (uv-m)*0.992; // subtle 3D shadow drop
vec2 mid_uv = m + (uv-m)*0.996;
ret = mix( vec3(1,0,2.2), vec3(0.3,0,1),texture( sampler_fw_main, bg_uv).y);
ret = mix(ret, vec3(3,1,2), max(((texture(sampler_blur1, bg_uv).xyz * scale1) + bias1).z*1.5,texture( sampler_main, mid_uv).z)); // if that isn't a cool glow effect?
ret *= 1-((texture(sampler_blur1, bg_uv).xyz * scale1) + bias1).x*3; // drop shadow
ret = mix( ret, vec3(3,0.01,0.4),texture( sampler_fw_main, uv).x);
//ret = texture(sampler_main, uv).xyz;
re = texture( sampler_MilkDrop3_017, my_uv).xyz;
re = mix( texture(sampler_MilkDrop3_016,my_uv).xyz, re, ret.x);
ret = mix( re, 1-re, ret.y);

//ret /= vec3(2.7,1.9,2.6);
ret /= vec3(-2.4,1.5,1.4);
}

warp_hlsl:
shader_body
{
    float4 N = tex2D(sampler_noise_lq, rand_frame.xy + uv*
                             texsize_noise_lq.zw*texsize.xy )*2-1;
    // sample previous frame
    ret = tex2D( sampler_pw_main, uv + N.zw*texsize.zw*0.5 ).xyz;
    ret += (0.59-0.9*rad)*N.xyz;


    ret = saturate((ret-0.5)*3 + 0.25);
    ret.yz = ret.x;
}

comp_hlsl:
sampler sampler_MilkDrop3_017;
sampler sampler_MilkDrop3_016;

shader_body
{


    float2 d = texsize.zw*12;
    float3 dx = ( GetBlur1(uv + float2(1,0)*d) - GetBlur1(uv-float2(1,0)*d) );
    float3 dy = ( GetBlur1(uv + float2(0,1)*d) - GetBlur1(uv-float2(0,1)*d) );
    float2 my_uv = uv + float2(dx.y,dy.y)*0.1;

float3 re;

//uv = uv + 0.02*float2(cos(uv.y*17),sin(uv.x*39));
float2 m = float2(1-q4,q8);
float2 bg_uv = m + (uv-m)*0.992; // subtle 3D shadow drop
float2 mid_uv = m + (uv-m)*0.996;
ret = lerp( float3(1,0,2.2), float3(0.3,0,1),tex2D( sampler_fw_main, bg_uv).y);
ret = lerp(ret, float3(3,1,2), max(GetBlur1(bg_uv).z*1.5,tex2D( sampler_main, mid_uv).z)); // if that isn't a cool glow effect?
ret *= 1-GetBlur1(bg_uv).x*3; // drop shadow
ret = lerp( ret, float3(3,0.01,0.4),tex2D( sampler_fw_main, uv).x);
//ret = tex2D(sampler_main, uv);
re = tex2D( sampler_MilkDrop3_017, my_uv);
re = lerp( tex2D(sampler_MilkDrop3_016,my_uv), re, ret.x);
ret = lerp( re, 1-re, ret.y);

//ret /= float3(2.7,1.9,2.6);
ret /= float3(-2.4,1.5,1.4);
}
