//ret = texture(sampler_main, uv).xyz;
re = texture( sampler_MilkDrop3_002, my_uv).xyz;
re = mix( texture(sampler_MilkDrop3_003,my_uv).xyz, re, ret.x);
ret = mix( re, 1-re, ret.y);

}

