float sigmoid(float t, float c){
   return 1 / ( 1 + exp(t*c));
}

vec2 vortex(vec2 domain, vec2 position, vec2 motionVector, vec4 domainAspect, float radius, float sharpness, float spin, float zoom){
   domain -= position;
   domain *= domainAspect.xy;
   float sigmoidFilter = sigmoid( length(domain) - radius , sharpness );
   domain +=  + motionVector*sigmoidFilter;
   spin *= sigmoidFilter;
   float s = sin(spin);
   float c = cos(spin);
   vec2 uv_rot;
   uv_rot.x = c*domain.x - s*domain.y;
   uv_rot.y = s*domain.x + c*domain.y;
   uv_rot *= domainAspect.zw;
   uv_rot += position;
   return clamp(position + (uv_rot-position)*mix(1,zoom,sigmoidFilter), 0.0, 1.0);
}

