#define sampler_pic sampler_Pattern_041
uniform sampler2D sampler_pic;

shader_body
{
    ret = texture( sampler_main, uv ).xyz;

    // ERROR DIFFUSION DITHER - looks great
    vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (texture(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*1;

    // mix in img...
    vec3 pic = texture(sampler_pic, uv_orig*aspect.xy).xyz;
    float lum = lum(pic);
    float use_it = abs(lum*0.8+0.1 - (0.5+roam_cos.y*0.25));
    float LFNoise = texture(sampler_noise_lq, uv*0.1 + time*0.01).x*6-2;
    use_it = clamp(1 - use_it*(3+9*rand_preset.x) - LFNoise, 0.0, 1.0);
    ret = mix(ret, pic, use_it.xxx);

    // darken over time
    //ret -= 0.004;
    //ret *= 0.99;
    ret *= 0.97 + 0.03*clamp( length(uv - uv_orig)*200 , 0.0, 1.0);  // center only
}

