frame_eqs_eel:
// raytraced sphere code

d = 20; // distance of the sphere, the farther you're away the more you'll see on the horizon
z = 4; // distance of the pane where the scene is projected on, wider distance = smaller apex angle of the camera, very small distances = fish eyes view

zz = 1 + bass_att*0; // linear zoom

pi = asin(1)*2;
pi2inv = 0.5/pi;
maskangle = asin( 1/d);
q32 = maskangle; // hit = (cameras angle < mask_angle)
q31 = d;
q30 = z;
q29 = pi2inv;
q28 = pi;
q27 = 1/zz;

v = 0.4;
j1 = j1*0.95 + sqr(bass*4)*v;
j2 = j2*0.95 + sqr(mid*4)*v;
j3 = j3*0.95 + sqr(treb*4)*v;

n = n + j1*0.0152;
n1 = n1 + j2*0.0152;
n2 = n2 + j3*0.0152;

vol=(bass+mid+treb)*0.25;
vol=vol;
q3=vol;
mtime = mtime+vol*0.01;
q1=mtime;

zoom = zoom + 0.015*vol;

w1 = n*0.01;    // solid angles to rotate the sphere in its origin
w2 = n1*0.01;
w3 = n2*0.01;

q20 = cos(w1);
q21 = sin(w1);
q22 = cos(w2);
q23 = sin(w2);
q24 = cos(w3);
q25 = sin(w3);

warp:
shader_body
{
    vec4 N = texture(sampler_noise_lq, rand_frame.xy + uv*
                             texsize_noise_lq.zw*texsize.xy ).xyz*2-1;
    // sample previous frame
    ret = texture( sampler_pw_main, uv + N.zw*texsize.zw*0.5 ).xyz;
    ret += (0.59-0.2*rad)*N.xyz;


    ret = clamp((ret-0.5)*3 - 0.05, 0.0, 1.0);
    ret.yz = ret.x;
}

comp:
float a,b,c;
shader_body
{
vec2 uv2 = uv;
if(uv2.x >= 0.5) {uv2.x = 1-uv2.x;}
if(uv2.y >= 0.5) {uv2.y = 1-uv2.y;}

//float cs = cos(0.25*3.14159);
//float sn = sin(0.278*3.424333);
//vec2 uv3 = uv2;
//uv2.x = uv3.x * cs - uv3.y * sn;
//uv2.y = uv3.x * sn + uv3.y * cs;

uv.x=uv2.x;
uv.y=uv2.y;


    vec3 base = ((texture(sampler_blur3, uv).xyz * scale3) + bias3) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2) + ((texture(sampler_blur1, uv).xyz * scale1) + bias1) + texture(sampler_main, uv).xyz*0.886 - 0.466 ;
a =base.x/12;b = base.y/8; c = base.x/3;
c = c + b + a-b;
a = a + c + a*c;
base = 0.2+c;
ret = base*(0.552-base)*(1-base)*2*vec3(0.55,1.688,2.25) + pow(base,2)*vec3(2.29,1.688,1.74); //MilkDrop2077 color mod
//ret.xy = ret.yx;
ret = ret*(1-ret)*4; //MilkDrop2077 solarize effect
//MilkDrop3 Color Mode:
ret /= vec3(-0.5,1.7,-2.1);
//MilkDrop3 Blow Mode:
vec3 avg_col = ((texture(sampler_blur2, uv).xyz * scale2) + bias2);ret = abs(avg_col - ret)*2;
}

