waves.frame_eqs_eel:
vol = bass*8 + mid*5 + treb*3;
m = m*0.97 + vol*0.08;
monitor = vol;
beat = above(vol,res)*above(vol,m)*above(vol,16);
diff = (1-beat)*diff + beat*(vol-res);
res = beat*(vol + m*0.04) + (1-beat)*(res -  (0.1+diff*0.02)*60/fps);
res = max(0,res);

a = beat;

pixel_eqs_eel:
du = (x*2-1) - q1;
dv = (y*2-1) - q2;
dist = sqrt(du*du+dv*dv);
ang2 = atan2(du,dv) + time*0.15;
mult = 0.6*sin(dist*0.05);
//dx = mult*sin(ang2*2-1.38)*aspectx;
//dy = mult*cos(ang2*2-1.5)*aspecty;

warp:
shader_body
{
    // sample previous frame
    ret = texture( sampler_main, uv ).xyz;

    // Custom effect - transformed from original code
    vec2 p = (uv.xy - 0.5*aspect.xy) / aspect.y;
    float3x2 mat = float3x2(6,6,6,6,6,6);
    float effect = (0.6 - length(fract((mat * p) + length(p) -time + 0.5) -0.1));
    ret += effect * aspect.y * 0.2;

    // darken (decay) over time
    ret *= 0.94; //or try: ret -= 0.004;
}

comp:
shader_body
{
    vec2 d = texsize.zw*4;
    vec3 dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
