warp:
shader_body
{
    float t = time;
    vec3 c = vec3(0.0);
    uv = (uv_orig - 0.5) * aspect.xy * 16.0;

    float i = 1.0;
    while (i < 64.0) {
        float s = fract(i * 0.1) * fract((i * 0.7 + t * 0.3));

        if (s <= 0.95) {
            float x = i * 0.1;
            float y = i * 0.25;
            float w = cos(t * 0.5 + i * 0.2) * 0.2;  

            float f = 0.5; // Base falling speed
            float a = fract(i * 0.2 + fract(x * 0.1) * fract(x * 0.3) * 100.0 + w);  
            float b = fract(i * 0.3 + fract(y * 0.1) * abs(time) / 200 * fract(y * 0.04) * 100.0);             
            vec2 p = -3.0 + 6.0 * vec2(a, b);
            float l = length(uv - p * 2.8);

            float r2 = fract(i * 0.5 * bass_att / 33) * fract(i * 0.1);
            float g2 = fract(i * 0.5 * bass_att / 33) * fract(i * 0.1);
            float b2 = fract(i * 0.5 * bass_att / 32) * fract(i * 0.9);
            vec3 o = vec3(r2, g2, b2) * 0.5;           
            c += o * 0.12 / (0.001 + l * l * 1.5);
        }

        i += 1.0;
    }

    ret =vec4(c, 1.0).xyz;
}

comp:
shader_body
{
    ret =texture(sampler_main, uv).xyz;
//MilkDrop3 Shadows Mode:
ret += pow(texture(sampler_main,vec2(uv.x,1-uv.y)).yyy+texture(sampler_main,1-uv).yyy,vec3(3,2,1.3));
}

