frame_eqs_eel:
decay=.6;
wave_a=0;
zoom=1;
warp=0;

q5=aspectx;
q6=aspecty;


bt=bt+bass/fps;
q1=bt;

warp:
shader_body
{
    // Calculate polar coordinates
    vec2 R = vec2(texsize.x, texsize.y);
    vec2 p = uv_orig * R * 2.0 - R;

    // Dynamic rotation speed based on bass (cubic easing for stronger response)
    float bassBoost = pow(bass_att, 3.0); // More aggressive acceleration
    float rotationSpeed = 0.79 * (1.0 + bassBoost/32);

    // Angle calculation with bass-accelerated animation
    float angle = atan(p.x, p.y) - time * rotationSpeed;

    // Enhanced color wave pattern with bass-reactive intensity
    vec4 color = (0.6 + 0.6 * cos(angle + vec4(0, 23, 21, 0))) 
                 * (0.02 + 0.01) / abs(length(p)/R.y - 0.5);

    // Boosted audio reactivity - affects both brightness and saturation
    color.rgb *= 1.0 + 0.3 * bass_att;
    //color.rgb = mix(color.rgb, color.rgb * 1.5, bass_att); // Extra punch

    ret = clamp(color, 0.0, 1.0); // Ensure colors stay in valid range
}

comp:
shader_body
{
ret = texture(sampler_main, uv).xyz;
ret*=1.2;
ret-=0.1;
}

