frame_eqs_eel:
// timed sidon sensor
// le = signal level; desired average value = 2
le=1.4*bass_att+.1*bass+.5*treb;
pulse=above(le,th);
// pulsefreq = running average of interval between last 5 pulses
pulsefreq=if(equal(pulsefreq,0),2,
if(pulse,.8*pulsefreq+.2*(time-lastpulse),pulsefreq));
lastpulse=if(pulse,time,lastpulse);
// bt = relative time; 0 = prev beat; 1 = expected beat
bt=(time-lastbeat)/(.5*beatfreq+.5*pulsefreq);
// hccp = handcicap for th driven by bt
hccp=(.03/(bt+.2))+.5*if(band(above(bt,.8),below(bt,1.2)),
(pow(sin((bt-1)*7.854),4)-1),0);
beat=band(above(le,th+hccp),btblock);
btblock=1-above(le,th+hccp);
lastbeat=if(beat,time,lastbeat);
beatfreq=if(equal(beatfreq,0),2,
if(beat,.8*beatfreq+.2*(time-lastbeat),beatfreq));
// th = threshold
th=if(above(le,th),le+114/(le+10)-7.407,
th+th*.07/(th-12)+below(th,2.7)*.1*(2.7-th));
th=if(above(th,6),6,th);

q8=10/fps;
ccl=ccl+beat;
minorccl=minorccl+q8*le;

q5=ccl+.0032*minorccl;
q6=ccl;

ccx=if(beat,.5+.2*sin(int(rand(354))),ccx);
cx=ccx+.1*sin(.01*minorccl);
q1=ccx;
ccy=if(beat,.5+.2*sin(int(rand(354))),ccy);
cy=ccx+.1*sin(.013*minorccl);
q2=ccy;
q3=4/th;
q4=.01*minorccl;
q7=th;

decay=1-q8*.02;
monitor=decay;


wx=if(pulse,int(rand(10))*.1,wx);
wave_x=wx;
