pixel_eqs_eel:
// coordinates transfomation
// q1,q2 = position; q3 = 1/zoom; q4 = rotation
aang=atan2(y-q2,x-q1)+q4;
arad=sqrt(sqr(y-q2)+sqr(x-q1))*q3;

radsector=(arad*4)%3;
angsector=(aang*1.273)%8;

zoom=1+q8*.1*sin(14*(angsector%4)*q6+radsector*q5);
rot=.1*q8*sin(q7)*sin(1.3*angsector*q5+2.7*radsector*q6);

