i=0;
loop (1024,
  z=rand(1)+.1;
  megabuf(i+2) = z;
  megabuf(i)  = (rand(1)-0.5)/z;
  megabuf(i+1)= (rand(1)-.5)/z;
i+=3;);

shapes.frame_eqs_eel:
