k1 = i/num_inst;
zmax = 7;
speed=.15;

z=megabuf(3*i+2);
x = megabuf(3*i)/z+.5;
y = megabuf(3*i+1)/z*reg32+0.5;

rad = .022/(z*zmax)*reg32 ;

out=(x>1)||(x<0)||(y>1)||(y<0)||(z<0);
if (out,
  megabuf(3*i)= (rand(2)-1); //x
  megabuf(3*i+1)=(rand(2)-1); //y
  megabuf(3*i+2)=1;
,
  megabuf(i*3+2)-=speed/fps;
);

a=1-pow(z,4);
a*=0.6;

waves.point_eqs_eel:
s=sample*6.28;

//plot random x position via function of sample pos;
xp=sin(s)+sin(s*0.14)+sin(s*32.3)+sin(s*9.8);
xp=xp*0.20;

//plot random y position via function of sample pos;
yp=cos(s)+sin(s*0.24)+cos(s*17.4)+sin(s*37.7);
yp=yp*0.20;

//plot random z position via function of sample pos;
zp=cos(s)+cos(s*5.24)+cos(s*47.4)+cos(s*27.7);
zp=zp*0.25;

//pull stars toward screen
zp=zp + 1 - t1;

//correct when below 0
zp=if( below(zp,0) , zp+2 , zp );

//darken far stars
a=(1 - zp*0.5);

zp=zp*0.7;

x_screen=xp/zp + 0.5;
y_screen=yp/zp + 0.5;

x=x_screen;
y=y_screen;

r=1;
g=1;
b=1;

frame_eqs_eel:
decay = 12.9;
warp = 0.1;

bassc = if(above(bass,bassc),bassc + bass*.07,bassc-.03);
midc = if(above(mid,midc),midc + mid*.07,midc-.03);
trebc = if(above(treb,trebc),trebc + treb*.07,trebc-.03);

q1 = bassc;
q2 = midc;
q3 = trebc;

warp:
shader_body{
    //Created By MilkDrop2077  

    // Relative position to ring light (left side)
    vec2 ring_coord = (uv_orig - vec2(0.5, 0.5)) * aspect.xy*1.3;

    // Compute SDF distance to ring
    float outer_dist = abs(length(ring_coord) - 0.4+q1*0.03);
    float middle_dist = abs(length(ring_coord) - 0.3+q2*0.04);
    float inner_dist = abs(length(ring_coord) - 0.2+q3*0.03);

    // Light intensity vectors
    vec4 primary_glow = vec4(0.9, 10.0, 10.0, 1.0);
    vec4 secondary_glow = vec4(0.2, 0.4, 2.0, 1.0); 
    vec4 accent_glow = vec4(10, 0.2, 0.2, 1.0); 

    // Combine lights with smooth attenuation
    vec4 final_color = primary_glow / (primary_glow + outer_dist*512.0) 
                       + secondary_glow / (secondary_glow + middle_dist*512.0) 
                       + accent_glow / (accent_glow + inner_dist*512.0);

    // Soft clip to prevent oversaturation
    final_color = 1.0 - exp(-final_color * 2.5);

    // Output with full alpha
    ret =clamp(final_color, 0.0, 1.0);
}

