frame_eqs_eel:
wave_a = 0;
//warp = 0;
zoom = 1;
bb = bb*0.99 + bass*0.02;
mm = mm*0.99 + mid*0.02;
tt = tt*0.99 + treb*0.02;

mx = max(max(bb,mm),tt);
mn = min(min(bb,mm),tt);

h1 = (bb-mn)/(mx-mn);
h2 = (mm-mn)/(mx-mn);
h3 = (tt-mn)/(mx-mn);

v = 0.15/fps;
bm = bm + (h1-h2)*v;
mt = mt + (h2-h3)*v;
bt = bt + (h1-h3)*v;

w = bm*2;
q3 = sin(w);
q4 = cos(w);


q10 = bm;
q11 = mt;
q12 = bt;

