comp:
shader_body
{float str=0.24;
//ret=texture(sampler_main,uv).zyx;
ret=texture(sampler_main, uv).xyz;
vec2 newuv;
vec3 m[3];
m[0]=texture(sampler_main, uv-(0.44-ret.xy)*str).xyz;
m[1]=texture(sampler_main, uv-(0.38-ret.yz)*str).xyz;
m[2]=texture(sampler_main, uv-(0.59-ret.xz)*str).xyz;
//ret=vec3(0.1,2.9,0.5),lum(m[1]),lum(m[2])); //MilkDrop2077 color mod
ret=(m[0]*m[1]*m[2])*1.2;
ret=ret*((texture(sampler_blur2, uv).xyz * scale2) + bias2);
ret/=0.23;
//MilkDrop3 Color Mode:
ret /= vec3(2.2,1.5,1.2);
//MilkDrop3 Solarize Mode:
ret =ret*(1-ret)*4;
//MilkDrop3 Brightness Mode:
ret *= 1.6;
}

