waves.point_eqs_eel:
s=sample*6.28;

//plot random x position via function of sample pos;
xp=sin(s)+sin(s*0.14)+sin(s*32.3)+sin(s*9.8);
xp=xp*0.20;

//plot random y position via function of sample pos;
yp=cos(s)+sin(s*0.24)+cos(s*17.4)+sin(s*37.7);
yp=yp*0.20;

//plot random z position via function of sample pos;
zp=cos(s)+cos(s*5.24)+cos(s*47.4)+cos(s*27.7);
zp=zp*0.25;

//pull stars toward screen
zp=zp + 1 - t1;

//correct when below 0
zp=if( below(zp,0) , zp+2 , zp );

//darken far stars
a=(1 - zp*0.5);

zp=zp*0.7;

x_screen=xp/zp + 0.5;
y_screen=yp/zp + 0.5;

x=x_screen;
y=y_screen;

r=1;
g=1;
b=1;

frame_eqs_eel:
//spiral collide frame
t = t + 2.5/fps;
t = if(above(t,2),t-2,t);
q22 = -t;
// Flexis dampened sound response functions
db = db*0.98 + bass_att*0.2; // dampener
bb = bb + db*0.1; // accumulation, replace time expressions with this ;)
dt = dt*0.98 + treb_att*0.2;
tt = tt + dt*0.1;
dm = dm*0.98 + mid_att*0.2;
mm = mm + dm*0.1;
//***** [q11-q18] variables for Flexis caleidoscope shader code *****
// change
q13 = 0.5 + sin((bb-mm)*0.1)*0.25; // center x
q14 = 0.5 + sin((tt-mm)*0.1)*0.25; // center y
w = (bb-tt)*0.1;                   // angle
q16 = 0.8 - (db-dt)*0.04;         // size
//don't change
q11 = sin(w);
q12 = cos(w);
q17 = sin(-w);
q18 = cos(-w);
q15 = 1/q16;
zoom =1;// 0.999;
warp = 0;


// a
scale = 8;
angle = -time*0.2;

q1 = scale*cos(angle); // the real part
q2 = scale*sin(angle); // the imaginary part

// b

q3 = 0; // the real part
q4 = 0.3; // the imaginary part

// c
scale = 0.8;
angle = time*0.2;

q5 = scale*cos(angle); // the real part
q6 = scale*sin(angle); // the imaginary part

// d
q7 = -0.25; // the real part
q8 = 0.2; // the imaginary part

q11 = 1/asin(1);

warp:
shader_body {
vec3 noise = texture(sampler_noise_mq,uv_orig*8+rand_frame).xyz;
float k1=0.7;
ret = max(texture(sampler_main, uv).xyz*k1,((texture(sampler_blur1, uv).xyz * scale1) + bias1));
ret = ret*0.98-0.01*lum(((texture(sampler_blur1, uv).xyz * scale1) + bias1))-0.01*(noise.x>0.85);
}

comp:
uniform sampler2D sampler_pw_noise_lq;
vec2 uv1,uv2,uv3,uv4,uv5,dz,uvw;

shader_body
{
uv1=(mat2(cos(q5),-sin(q5),sin(q5),cos(q5)) * uv-0.5);
float z=1/(uv1.y);
vec2 uv2 = vec2 (uv1.x*z,z)*.25;
vec2 water = texture(sampler_noise_mq,uv2/2+vec2(0,time/2)).xyz/z;

uvw= uv1*vec2 (1,.5)+0.5+water*0.2*(z>0);
uvw = fract (uvw);

ret = (texture(sampler_main, uv).xyz + ((texture(sampler_blur2, uv).xyz * scale2) + bias2) * (0.75+abs(uv1.y)*3));
ret += clamp(1-2*lum(ret), 0.0, 1.0) * pow(((texture(sampler_blur1, uvw).xyz * scale1) + bias1),2)*clamp(.4/z, 0.0, 1.0)*64*clamp(1-rad, 0.0, 1.0);
ret *= 1.1;

uv=1-uv;
uv1=(uv-sin(q5/3))*aspect.xy;
uv3=(uv-tan(q5/3))*aspect.xy;
uv5=(uv-sin(q5/6))*aspect.xy;
ret += 0.005/length(uv1.x-0.3) + 0.005/length(uv3.x-0.5) + 0.005/length(uv5.x-0.4);
ret *=1.5;
ret -=0.07;
//MilkDrop3 Color Mode:
ret /= vec3(1.6,-2.7,-2.5);
}

warp_hlsl:
shader_body {
float3 noise = tex2D(sampler_noise_mq,uv_orig*8+rand_frame);
float k1=0.7;
ret = max(GetPixel(uv)*k1,GetBlur1(uv));
ret = ret*0.98-0.01*lum(GetBlur1(uv))-0.01*(noise.x>0.85);
}

comp_hlsl:
sampler sampler_pw_noise_lq;
float2 uv1,uv2,uv3,uv4,uv5,dz,uvw;

shader_body
{
uv1=mul (uv-0.5, float2x2(cos(q5),-sin(q5),sin(q5),cos(q5)));
float z=1/(uv1.y);
float2 uv2 = float2 (uv1.x*z,z)*.25;
float2 water = tex2D(sampler_noise_mq,uv2/2+float2(0,time/2))/z;

uvw= uv1*float2 (1,.5)+0.5+water*0.2*(z>0);
uvw = frac (uvw);

ret = (GetPixel(uv) + GetBlur2(uv) * (0.75+abs(uv1.y)*3));
ret += saturate (1-2*lum(ret)) * pow(GetBlur1(uvw),2)*saturate(.4/z)*64*saturate(1-rad);
ret *= 1.1;

uv=1-uv;
uv1=(uv-sin(q5/3))*aspect.xy;
uv3=(uv-tan(q5/3))*aspect.xy;
uv5=(uv-sin(q5/6))*aspect.xy;
ret += 0.005/length(uv1.x-0.3) + 0.005/length(uv3.x-0.5) + 0.005/length(uv5.x-0.4);
ret *=1.5;
ret -=0.07;
//MilkDrop3 Color Mode:
ret /= float3(1.6,-2.7,-2.5);
}
