comp:
vec2 uv1,uv2,uv4,dz,uvw;

float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body
{
uv1=(mat2(cos(q5),-sin(q5),sin(q5),cos(q5)) * uv-0.5);
float z=1/(uv1.y);
vec2 uv2 = vec2 (uv1.x*z,z)*.25;
vec2 water = texture(sampler_noise_mq,uv2/2+vec2(0,time/2)).xyz/z;

uvw= uv1*vec2 (1,.5)+0.5+water*0.2*(z>0);
uvw = fract (uvw);

ret =(texture(sampler_main, uv).xyz + ((texture(sampler_blur3, uv).xyz * scale3) + bias3) * (0.5+abs(uv1.y)*3));
ret += clamp(1-2*lum(ret), 0.0, 1.0) * _pow(((texture(sampler_blur1, uvw).xyz * scale1) + bias1),2)*clamp(.4/z, 0.0, 1.0)*64*clamp(1-rad, 0.0, 1.0);
ret *= 1.1;

uv=1-uv;
uv1=(uv-.5)*aspect.xy;
ret += 0.01+(bass_att/64)/length(uv1-0.25);
ret-=0.05;
}

