waves.point_eqs_eel:
//q1 = state;
//q2 = basstime;

q1 = 0;
speed = bass_att;
v = sample*1000000 + value2*bass*0.1;

//state 0 elipse
xs = xs + sin(v)*speed*atan(v*1.51);
ys = ys + sin(v)*speed*atan((v));

x = 0.5 + 0.5*sin(xs*0.1)*cos(time*2 + xs);
y = 0.5 + 0.5*sin(ys*0.1)*cos(time*2.1 + xs);

x = x*0.6 + 0.3;
y = y;
y = y*0.8;

r = 1;//0.5 + 0.5*sin(time*1.22) + 0.1;
g = 1;//0.4 + 0.4*sin(time*1.307);
b = 1;//0.4 + 0.4*sin(time*1.959);

xs = if(above(xs,1000),0 ,xs);
ys = if(above(ys,1000),0 ,ys);

frame_eqs_eel:
chng=sin(time*.5);
cthr=.9999;
mq21=if(above(chng,cthr),rand(3),mq21);
mq22=if(above(chng,cthr),rand(3),mq22);
mq23=if(above(chng,cthr),rand(3),mq23);
mq24=if(above(chng,cthr),rand(2),mq24);
mq25=if(above(chng,cthr),rand(2),mq25);
mq26=if(above(chng,cthr),rand(2),mq26);
mq27=if(above(chng,cthr),rand(1),mq27);
mq28=if(above(chng,cthr),rand(1),mq28);
mq29=if(above(chng,cthr),rand(1)*.3,mq29);
mq31=if(above(chng,cthr),rand(1)*.3,mq31);
monitor=chng;
q21=mq21;q22=mq22;q23=mq23;q24=mq24;q25=mq25;q26=mq26;
q27=mq27;q28=mq28;q29=mq29;q31=mq31;

monitor=mq1;
vol=bass+treb+mid;
atime=atime+vol;
q11=.4+sin(atime*.006        )*.4;
q12=.4+cos(atime*.00613828348)*.4;
q13=.4+sin(atime*.00598593455)*.4;
monitor=q13;

zoom = 1;
warp = 0;
wave_a = 0;

// elastic thread code

xx1 = xx1*0.9 + (bass)*0.01;
xx2 = xx2*0.9 + (treb)*0.01;
yy1 = yy1*0.94 + (treb+bass)*0.0075;
x1 = 0.5 + (xx1-xx2)*2;
y1 = 0.4 + yy1*1.5;
dt = 0.03/fps;
vx2 = vx2*(1-2*dt) + dt*((x1+x3-2*x2)*10);
vy2 = vy2*(1-2*dt) + dt*((y1+y3-2*y2)*10-0.5);
vx3 = vx3*(1-2*dt) + dt*((x2+x4-2*x3)*10);
vy3 = vy3*(1-2*dt) + dt*((y2+y4-2*y3)*10-0.5);
vx4 = vx4*(1-2*dt) + dt*((x3-x4)*10);
vy4 = vy4*(1-2*dt) + dt*((y3-y4)*10-0.5);
x2 = x2 + vx2; y2 = y2 + vy2;
x3 = x3 + vx3; y3 = y3 + vy3;
x4 = x4 + vx4; y4 = y4 + vy4;
vx2 = if(above(x2,0),vx2,abs(vx2)*0.5);
vx2 = if(below(x2,1),vx2,-abs(vx2)*0.5);
vx3 = if(above(x3,0),vx3,abs(vx3)*0.5);
vx3 = if(below(x3,1),vx3,-abs(vx3)*0.5);
vx4 = if(above(x4,0),vx4,abs(vx4)*0.5);
vx4 = if(below(x4,1),vx4,-abs(vx4)*0.5);
vy2 = if(above(y2,0),vy2,abs(vy2)*0.5);
vy2 = if(below(y2,1),vy2,-abs(vy2)*0.5);
vy3 = if(above(y3,0),vy3,abs(vy3)*0.5);
vy3 = if(below(y3,1),vy3,-abs(vy3)*0.5);
vy4 = if(above(y4,0),vy4,abs(vy4)*0.5);
vy4 = if(below(y4,1),vy4,-abs(vy4)*0.5);
q1 = x1;q2 = x2;q3 = x3;q4 = x4;
q5 = y1;q6 = y2;q7 = y3;q8 = y4;
q9 = 1/aspectx;
q10 = 1/aspecty;
q11 = aspectx;
q12 = aspecty;

// coloring borders
bb = bb*0.99 + bass*0.02;
mm = mm*0.99 + mid*0.02;
tt = tt*0.99 + treb*0.02;

mx = max(max(bb,mm),tt);
mn = min(min(bb,mm),tt);

ob_r = (bb-mn)/(mx-mn);
ob_b = (mm-mn)/(mx-mn);
ob_g = (tt-mn)/(mx-mn);

v = 0.4/fps;
bm = bm + (ob_r-ob_b)*v;
mt = mt + (ob_b-ob_g)*v;

// coordinates spiral 1
q21 = 0.5; // X
q22 = 0; // Y

// coordinates spiral 2
q23 = -0.5;
q24 = 0;

q25 = 0.5/asin(1); // pi/4

q26 = -bm; // rotation
q27 = mt; // growth
q30=bass+treb+mid;

warp:
shader_body
{
    vec2 scale = vec2(1280,1024)*texsize.zw; // 1280x1024 : the resolution i modelled this shader with
    float d = 0.005;
    float dx = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).x*scale.x;
    float dy = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).x*scale.y;
    float dxb = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).x*scale.x;
    float dyb = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).x*scale.y;
    vec2 my_uv = uv - vec2(dx,dy)*0.01 + vec2(dxb,dyb)*0.003;



    vec2 v = 0.012;
    ret.x = texture( sampler_fw_main, my_uv -floor(my_uv)*0).x;


    ret.x += (ret.x - ((texture(sampler_blur3, uv).xyz * scale3) + bias3).x)*.1;
    ret.x += 0.004;


//--------------------------------



//--------------------------------

    d = 0.01;
    my_uv = vec2(-dy,dx)*0.045;

    dx = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).y*scale.x;
    dy = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).y*scale.y;
    my_uv += uv - vec2(dx,dy)*0.03;



    v = 0.01;
    ret.y =texture( sampler_fw_main, my_uv).y;


    ret.y += (ret.y - ((texture(sampler_blur3, my_uv).xyz * scale3) + bias3).y)*0.2 + 0.0;
    ret = mix(ret, texture(sampler_main,uv_orig).xyz, 0.2); //motionblur

//ret = 0;
}

comp:
vec2 d, uv_y;
vec3 dx, dy;
vec2 numerator, denominator, fraction;

shader_body
{



float cutoff = 0.95;
int n = -1;
float s = 0.4;

   vec2 c = (uv-0.5)*2*aspect.wz;
   numerator = (c + vec2(q21,q22))*64;
   denominator = c + vec2(q23,q24);
   fraction = vec2( numerator.x*denominator.x + numerator.y*denominator.y,
                      numerator.y*denominator.x - numerator.x*denominator.y)/
                      (denominator.x*denominator.x + denominator.y*denominator.y);
   c = fraction-0.5;
   float ang_hq = n*atan(c.x,c.y);
   vec2 spiral = vec2(ang_hq*q25+q26,s*aspect.w*log(length(c))+ang_hq*q25+q27);
   spiral = 0.5 + (0.5 - abs( fract( spiral * 0.5 ) * 2.0 - 1.0 ))*cutoff; // mirror


vec2 uvo = uv;
uv = spiral;

    d = texsize.zw*8;
    dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

vec3 base = texture(sampler_main, uv).xyz;
vec3 emboss = (-dx + dy + 1)*1.2;

    d = texsize.zw*2;
    dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

float grad = length(vec2(dx.x,dy.x));

vec4 noise9 = texture(sampler_noisevol_hq, 
                      ((vec3(dx.y,dy.z,dy.x)*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1,1,0)*0.05 + 
                      time*vec3(0,0,1)*q31
                     ).xyz;
if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
ret = mix( vec3(0.5,0.4,0.6)*base.x*emboss.x,
            vec3(8,5,2)*grad,
            grad*4.2
          ); 
ret = mix(ret,4*ret,texture(sampler_main, uv).xyz.y*0.5-((texture(sampler_blur1, uv).xyz * scale1) + bias1).z*0.3*vec3(0.3,1,1));

grad = length(vec2(dx.z,dy.z));
ret = mix(ret, vec3(2,2,0), grad*0.7);
//MilkDrop3 Color Mode:
ret /= vec3(1.4,1.4,0.9);
}

