init_eqs_eel:
n = 0;
loop (10000,megabuf(n)=0;gmegabuf(n)=0;n += 1;);

posx = 0; posy = rand(1000);;
ranx = rand (1); rany = rand(1);
ranh = rand(10);
t0 = time;
st = 0.5; st_=0.5;

frame_eqs_eel:
st = max (0, st-0.2/fps);
dec = 1-0.5/fps;
st_ = st_* (dec) + (1-dec)*st;

//dir
dir = .3*sin(time*.0262+rany*6);
q2 = dir+0.5; 
q3 = tan(dir);
q4 = cos(dir);

//scaling plane
q7 = 0.002*0.27;

//movement
dpx = 3;
dpy = 0.007/q7;
posy += dpy/fps;
q10 = posx; q11 = posy;
q12 = dpx/fps ; q13 = dpy/fps;

//start ran for warp mod 
q15 = ranx+time/122;
q16 = rany;

test = (t0-time)*0.047-3.14/2;
view = 0.7 + min(1, max (-1, 2*sin(test)))*0.17;

//Alti
q8 = 1;
q6  = 0.6;

q20 = -0.05 * abs (sin(ranh+posy*0.0005));
q21 = 0.6 + 0.3*  cos(ranh+posy*0.0063); 
q22 = 0.15 + 0.05*  cos(ranh+posy*0.0028);

warp:
#define sat saturate
#define snl sampler_noise_lq
const float quality = 3, a = aspect.x/aspect.y, k1=16, k2 = 0.04, smod = q20, xmod=q22;
vec3 rsl, rsl3, ret1, sky, mod,mod2;
vec2 dz, uv0, uv1, uv2, uv3, uv4, uvc, dz1;
float  lav_gnd,lav_gnd2, smask, ripple, l,d,i,p;
const float hory = q6, xyscale = q7, alti = q8;
const vec2 hor = texsize.zw*vec2 (1.5,0), dir = vec2 (q3,q4), modran = vec2 (q15,q16);
const vec2 dpxy = vec2 (q12, q13);
const vec3 mov = vec3 (q10,q11,0);

vec2 noise2 (vec2 uvi) {vec2 tmp = vec2(1); float s = 1; uvi*=xyscale*32;
  for (int n = 1; n <= 2; n +=1 ) {tmp *= 1-texture(snl,uvi*s).xyz; s*= 3;} return tmp;}

vec2 noise3 (vec3 uvi, vec3 mod) {float tmp = 1; float s = 2; float diff,path;

 tmp = (texture(sampler_noise_mq,uvi*xyscale*vec2(1,.5)+modran.x).r-0.6)*mod.z*2.5; 
  diff = tmp;
  for (int n = 1; n <= 4; n +=1 ) {tmp += texture(snl,uvi.xy*s*xyscale).xyz/s/2; s*= 3;}
  tmp = mix (0.3,tmp,clamp(+_pow(uvi.x*xmod,2), 0.0, 1.0));
  tmp = mix (0.2,tmp,1-_pow(sin(smod*abs(uvi.x)+(uvi.y*.02+(1-tmp)*mod.x)),8));
 return 1-pow(tmp,0.8+mod.y);}

vec3 rs_lav (vec2 uvi, float berg) {  float z; vec2 rs;
  z = 1/(uvi.y+hory)*(alti-berg*k1); 
  z *= z * k2;
  rs.x = uvi.x * a*z ; rs.y = z; 
  return vec3(rs,z)+mov; }


float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body {
uv = uv_orig; uv1 = uv-0.5;
lav_gnd=  -min (texture(sampler_fc_main,uv).b, texture(sampler_fc_main,uv+hor.yx*1.5).b);
rsl = rs_lav(uv1,lav_gnd); 
mod = texture(sampler_noise_hq,rsl.xy*xyscale+modran).xyz;
i=0, p=0;
for (int n=1; n <= quality; n++) {
  p =  ((lav_gnd+noise3 (rsl, mod))/quality);
  i += p;  lav_gnd -= p*0.1+i*0.07; rsl = rs_lav(uv1,lav_gnd);}

l = clamp(-lav_gnd, 0.0, 1.0);
d= 2;
dz = -1/rsl.z*d*dir;
ripple = (noise3 (rsl,mod) - noise3 (rsl+vec3 (dz,0),mod))*sat(rsl.z/16)*42;
ripple += ((noise2 (rsl-dz) - noise2 (rsl+dz))* (1-l))+0.05;
ripple = 1-exp(-ripple*2);

ret =vec3 (ripple, sqrt (rsl.z)*0.05, l);

float tmp = dpxy.y + rsl.z;
uv.x += -(uv.x-0.5)*dpxy.y / tmp;
uv.y += (alti+l*k1) * (sqrt(k2/(tmp))-sqrt(k2/rsl.z));

ret =mix (ret, texture(sampler_fc_main,uv).xyz-0.004, vec3 (0.75,0,.2));
}

