shapes.frame_eqs_eel:
vis = bnot(q27%8)*q22/2;
vis = min(vis,1);
a = vis/2; a2 = vis/2;
//tex_ang = 3*(1-vis);

tex_zoom = 1;
rad = vis*.3;

steps = q28+1;
steps = 4;
ampl = .2; //.2 bis .5 praktisch

x = .5 + ampl*cos((q27/steps+instance/4)*6.28);
y = .5 + ampl*sin((q27/steps+instance/4)*6.28);

a = .4; a2 = .0;


trig = q24 * bnot(q27%4);
red = bnot(trig)*red+ (rand(100)/100)*trig;
green = bnot(trig)*green+ (rand(100)/100)*trig;
blue = bnot(trig)*blue+ (rand(100)/100)*trig;

k1 = instance / num_inst;

r = red *k1;
g = green * (1-k1);
b = blue * 4*k1*(1-k1);

r = red;
g = green;
b = blue;



r2 = r; b2 = b; g2 = g;

init_eqs_eel:
speed_=1;vx = 0; vy = 0; vz = 0;
trel = 0; p1 = 0; dir = 0;
ang0 = 0;

frame_eqs_eel:
dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.96, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .2+avg+peak) * above (time, t0+.5);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %16;
index2 = (index2 + is_beat*bnot(index))%8;

q20 = avg;
q21 = beat;
q22 = peak;
q24 = is_beat;
vol = bass_att + mid_att + treb_att;
vol_ = dec_med * vol_ + (1-dec_med)* vol;
vol__ = dec_med * vol__ + (1-dec_med)* vol_;
q27 = index + 1;
q28 = index2 + 1;

k1 =  is_beat*equal(index%4,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.1416/4;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

trig = q24 * bnot(index%8);
vx = vx*bnot(trig) + trig* (rand(100)/100-.5);
vy = vy*bnot(trig) + trig* (rand(100)/100-.5);
vz = vz*bnot(trig) + trig* (rand(100)/100-.5);

q10 = 0.2+vy*vy*4;
q11 = cos(time/9)*.5+.3;

speed = min(vol_-vol__+.5,1);
speed = max(speed,-.5);
speed_ = bnot(trig)*speed_ + trig*speed;
movz = movz + 1/fps*speed_;
q9 = movz;

q12 = min(q22,6)*2;
//q13 = 1.5+sin(time/5);
trig = bnot(index%4) * q24;
dir = bnot(trig)*dir + trig*(rand(10)-5);

trel = trel + .2/fps*dir;

q5 = cos(trel);
q6 = sin(trel+0*q27*(q28%2));
q7 = -q6;
q8 = q5;

px = px * dec_med + (1-dec_med)*vx;
py = py * dec_med + (1-dec_med)*vy;
q15 = px/3;
q16 = py/3;

q13 = min(2,1+abs(8*px*py));

ang0 = ang0*dec_med + vz*(1-dec_med);
q17 = ang0*2; 

zoom = 1.1;
rot = .2*sin(bnot(q28%2)*q28) ;
dx = .00;

monitor = q11;

warp:
