shapes.frame_eqs_eel:
x = 0.5 + gmegabuf(8+instance/2)*2;
y = 0.5 + gmegabuf(16+instance/2)*2;;

//x = 0.1*int(x*10);
//y = 0.1*int(y*10);

a = max(0,min(1,q15/(2+instance)-0.5));
a = (q15>1.5*(1+instance));
rad = 0.1 * q14;

init_eqs_eel:
a1 = 0; a2 = 0;
n = 0;
loop (5000,
  megabuf(n)=0;
  gmegabuf(n)=0;
  n += 1;
);

bx = 0; by = 0;
start = 1;

frame_eqs_eel:
dt = 1/fps; dec_m = 1 - 4*dt; dec_s = 1 - dt;
v0 = (bass+mid+treb)/3;
if (vol > v0, dec_f=0.5,dec = 1-8*dt);
vol = vol*dec_f + (1-dec_f)*v0; 
vol_ = vol_*dec_m+vol*(1-dec_m);
tvol = tvol*0.8 + 0.2*(vol-vol_);

is_beat = above(vol, peak) * above (time, t0+.25);
if (is_beat,t0=time; peak=vol*1.5,Peak*=dec_s);
bindex = (bindex + is_beat) %4;
bindex2 = (bindex2+is_beat*!bindex)%32;

//delay chain
n = 150;
loop (150, gmegabuf(n)=gmegabuf(n-1); n-=1);
gmegabuf(0) = tvol;
q15 = tvol/2;

num_res = 80; MINBPM = 120; MAXBPM = 200;
maxind = 0; qavg = 0.001; i=0; avg = 0.001;
loop (num_res,
  n = i*8;
  Freq  = (i/(num_res-1)+minbpm/(maxbpm-minbpm))*(maxbpm-minbpm)/60;
  Feder = sqr(Freq*6.2832); //Res.f = sqrt (Feder)/2pi (Hz)
  Excite = tvol;
  megabuf (n+4) = megabuf(n+2); //alte pos
  megabuf (n+1) *= 1-0.22*dt; //damping
  EC_Steps = int (20*Freq*dt)+1;
  loop (EC_Steps, // Euler-Cauchy steps
    Kraft = Excite - Feder*megabuf(n + 2) ;
    megabuf (n+1) += Kraft        * dt/EC_Steps;
    megabuf (n+2) += megabuf(n+1) * dt/EC_Steps; );
  beatcos = megabuf(n+1);  beatsin = megabuf(n+2)*Freq*6.2832;
  quad = sqrt (pow(beatsin,2) + pow(beatcos,2));
  megabuf (n+3) = megabuf(n+3) *dec_m +  quad* (1-dec_m);
  if (megabuf(n+3) > megabuf (maxind*8+3), maxind =i,0);
  qavg = qavg+pow (megabuf(n+3),2);
  avg =  avg + megabuf(n+3);
  i += 1;);

qavg = sqrt (qavg/num_res); avg /= num_res;
qdev = 0; i =0; loop (num_res,  qdev += pow(megabuf(i+3)-qavg,2); i += 8;);
qdev = sqrt (qdev/num_res);
certain  = megabuf(maxind*8+3)/qavg;
dbl_beat = megabuf(maxind*8+2) * megabuf (maxind*8+4) < 0;
bcount += dbl_beat;

t= 0 ; q1 = sin(t); q2 = cos(t); q3 = -q2; q4 = q1;
k1 = 0.03;
v1 = bass*k1; v2 = mid*k1; v3 = treb*k1;
c1 = cos(v1); c2 = cos(v2); c3 = cos(v3); 
s1 = sin(v1); s2 = sin(v2); s3 = sin(v3);
q20 = c2*c1; q21 = c2*s1; q22 = -s2;
q23 = s3*s2*c1 - c3*s1; q24 = s3*s2*s1 + c3*c1; q25 = s3*c2;
q26 = c3*s2*c1 + s3*s1; q27 = c3*s2*s1 - s3*c1; q28 = c3*c2;

trig = is_beat && (bindex2%2==0) && (bindex==0);
ang1 =if (trig, int(rand(2))*3.1416, ang1);;
ang2 =if (trig, int(rand(4))*3.1416/2, ang2);;
conv =if (trig, rand(1)>0.5, conv);;
tr1 = tr1*0.8+0.2*ang1;
tr2 = tr2*0.8+0.2*ang2;
conv_ = conv_*0.8+0.2*conv;
q5 = conv_;

ss = ss*0.9 + 0.1*max (0.3, (vol_>0.1)* (start>1)); ;
b1 = b1*0.8 + 0.2* ss*.8; //geschw
b2 = b2*0.8 + 0.2* ss / (0.8+0.2*max(1,vol*2-0.5)); //gr.

q10 = cos(tr1)*b1;
q11 = sin(tr1)*b1;
ct = if (ct, certain >3, certain>4) * (bindex2 < 12);
jump = bcount * ct;
q8 = cos(tr2+jump*2)*b2;
q9 = sin(tr2+jump)*b2;
fcountx += q10; fcounty += q11;
q12 = -fcountx; q13 = -fcounty;

q14 = sqrt(bass+mid+treb)/2;
q15 = peak;
q16 = vol-vol_;
q32 = aspecty;
start += dt;

warp:
