vec3 Get1 (vec2 uvi, float par)
{return ((texture(sampler_blur1, uvi).xyz * scale1) + bias1);}

vec3 argo (vec2 uvi)
{
float k1 = sqrt(abs(1-ir0*ir0*uvi.y*uvi.y));
float k2 = 1/k1;  kk = k2;
k1 = mix(k1,k2,q5);
vec2 rs = vec2 (asin(uvi.x*ir0/k1)*2/M_PI, (uvi.y*ir0/k1))/scale;
return atan (0.5*vec3 (rs,ir0*length(rs)>2))*2;
}

shader_body {
vec2 uv0 = uv;
uv = (uv-0.5)*(1+rad*0.5)*0.8+0.5;
uv1 = (mat2(q8,q9,-q9,q8) * (uv-0.5)*aspect.xy);
rs = argo(uv1)+0.5;

vec3 struc = texture(sampler_main, rs).xyz * clamp(ir0/kk-0, 0.0, 1.0);
//struc = pow(struc,0.8);
