shapes.frame_eqs_eel:
tex_zoom=1.5-bass_att*.4;
additive=if(equal(frame%(4-bass),0),1,0);
bb=bb+0.00174*(bass+bass_att+q2+treb_att+mid_att);
tex_ang=5.401*(sin(0.98*bb*q1*q2)*0.48+1+cos(10.48*bb)*0.48);


rad=bb/frame-.8;
tri=if(above(bass,treb),1,0);
squ=if(above(treb,bass),1,0);
cir=if(band(above(mid,treb),above(mid,bass)),1,0);

sides=3*tri+4*squ+300*cir;

ang=bass_att*mid_att-treb_att*mid_att;

rr=bass_att;
gg=treb_att;
bb=mid_att;


r22=rr;
b22=bb;
g22=gg;


rr=br;
gg=bg;
bb=bo;

br=.6+sin(bb*q2+bb*q4)*.4;
bg=.6+sin(bb*q6+bb*q1)*.4;
bo=.6+sin(bb*q2*q4*q6+bb*q1*q3*q5*100)*.4;

border_r=br;
border_b=bo;
border_g=bg;


r=1-rr;
r2=b22;
b=1-bb;
b2=g22;
g=1-gg;
g2=r22;

r = q1;
g = q2;
b = q3;
r2 = q2;
g2 = q3;
b2 = q1;
x = x + 0.11*cos(time) + 0.121*sin(1.2*time) + 0.15*sin(0.63*time);
y = y + 0.134*cos(1.13*time) + 0.2*sin(0.8*time) + 0.213*sin(0.43*time);
border_r = q3;
border_b = q2;
border_g = q1;
ang = ang + 2.52*cos(time);

vol=(bass+mid+treb_att)*0.3333;

redsine=.5+.15*bass*sin(time*3);
greensine=.5+.15*mid*sin(time*2);
bluesine=.5+.15*treb*sin(time);

redif=if(above(bass,1.2),redsine,if(above(redif,.95),0,redif*.85));
greenif=if(above(mid,1.2),greensine,if(above(greenif,.95),0,greenif*.85));
blueif=if(above(treb,1.2),bluesine,if(above(blueif,.95),0,blueif*.85));

r=redif;
g=greenif;
b=blueif;

r2=sin(time*.47);
g2=sin(time*.72);
b2=sin(time*0.33);

tex_ang=3.14+3.14*sin(time*.42);
ang=3.14+3.14*sin(time*.55);

frame_eqs_eel:
decay=.6;
wave_a=0;
zoom=1;
warp=0;

q5=aspectx;
q6=aspecty;


bt=bt+bass/fps;
q1=bt;

warp:
float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
